/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "rsy4070-service:searchCurWeekNo" ) {
		if (dsCurWeek.rowcount() > 0) {
		    sCurWeekNo = dsCurWeek.GetColumn(0, "CUR_WEEK_NO");
		    fcSearch();
		}
	}

	// 주간업무조회 완료 후
	if ( sServiceName == "rsy4070-service:searchWeekDuty" ) {
		if (dsWeekDuty.rowcount() > 0) {
			calStrDt.value = dsWeekDuty.GetColumn(0, "WEEK_STR_DT");
			calEndDt.value = dsWeekDuty.GetColumn(0, "WEEK_END_DT");
		}
	}

	 // 주간업무 저장 완료 후
	if ( sServiceName == "rsy4070-service:saveWeekDuty" ) {

	}	
}

function fcOnClick(obj)
{
	switch (obj.id) {
		case  "btnPrev":
			sCurWeekNo --;
			fcSearch();
			break;			
		case  "btnNext":
			sCurWeekNo ++;
			fcSearch();
			break;
	}

}

function fcOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sParam = " USER_ID="+dsWeekDuty.GetColumn(nRow,"EMP_NO");

	GBL_RET_VALUE=sParam; // 값 생성		
	global.FrameBottom.fcOpenMdiForm("RSY4080", "업무일지", "frm_rsy::RSY4080.xml");

}
