/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

    stUserId.Text = sUserID;

	 sServiceName = "rsy4080-service:searchDutyList";
	 sInDataSet   = "";
	 sOutDataSet  = "dsDutyList=dsDutyList ";

	 //조회조건 : 
	 fcd_AddParam("USER_ID",     sUserID);
	 fcd_AddParam("STR_DT",      calStrDt.Value);
	 fcd_AddParam("END_DT",      calEndDt.Value);
     fcd_AddParam("STAT_CD",     cboStatCd.Value);
     fcd_AddParam("END_YN",      cboEndYn.Value);
     fcd_AddParam("SEARCH_WORD", edSearchWord.Text);     

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsReportMana) && !fnc_DatasetChangeCheck(dsReportMng) && !fnc_DatasetChangeCheck(dsUploadFile)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsDutyList;
	sMandatoryColID   = "DUTY_DT|JOB_TYPE|DUTY_TITLE";
	sMandatoryColName = "등록일자|업무구분|작업제목";
	oGrid             =  grdDutyList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	//  완료일자가 입력된 경우 처리상태가 등록이나 진행중이면 완료로 상태 변경처리
	for (var iCnt=0;iCnt<dsDutyList.rowcount();iCnt++) {
		if (!fnc_IsNull(dsDutyList.GetColumn(iCnt, "END_DY"))) {
			if (dsDutyList.GetColumn(iCnt, "STATUS") == "001" ||
			    dsDutyList.GetColumn(iCnt, "STATUS") == "002" ) {
				dsDutyList.SetColumn(icnt, "STATUS", "003");
			}
		}
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	switch ( obj.id ) {
		case "btnSave":
			if ( !fcSaveCheck() ) return;

			sServiceName = "rsy4080-service:saveDutyList";
			sInDataSet   = "dsDutyList=dsDutyList:u";			
			sOutDataSet  = "dsDutyList=dsDutyList ";

			 // 재조회조건 : 
			 fcd_AddParam("USER_ID",     sUserID);
			 fcd_AddParam("STR_DT",      calStrDt.Value);
			 fcd_AddParam("END_DT",      calEndDt.Value);
			 fcd_AddParam("STAT_CD",     cboStatCd.Value);
			 fcd_AddParam("END_YN",      cboEndYn.Value);
			 fcd_AddParam("SEARCH_WORD", edSearchWord.Text);     

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);			 
			break;
	}
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" : //보고서 그리드 행추가,삭제 
			nRow = dsDutyList.AddRow(); // Record Add
			dsDutyList.SetColumn(nRow, "USER_ID", gdsUser.GetColumn(0, "USER_ID") );			
			dsDutyList.SetColumn(nRow, "DUTY_DT", today() );			
			dsDutyList.SetColumn(nRow, "DUE_DT", today() );			
			dsDutyList.SetColumn(nRow, "STATUS", "001");		// 상태 : 등록	
			dsDutyList.SetColumn(nRow, "IMPTC", "002");			//중요도 :보통
			edDutyTitle.SetFocus();
			break;
	}
}



/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :	//보고서 그리드 행추가,삭제 
			if ( !fnc_DeleteCheck(dsReportMana, "chk", "1") ) return;
			for (iCnt=dsDutyList.rowcount();iCnt>=0;iCnt--) {
				if (dsDutyList.GetColumn(icnt, "CHK") == "1") {
					dsDutyList.DeleteRow(iCnt);
				}
			}	
			break;			
	}
}



