queryMap.query:
SELECT MEET_CD, DECODE(COMMI,NULL,'TOTAL',COMMI) COMMI, CASE WHEN COMMI='COMMI_1' THEN '수득금' WHEN COMMI='COMMI_2' THEN '경주세' WHEN COMMI='COMMI_3' THEN '교육세' WHEN COMMI='COMMI_4' THEN '농특세' WHEN COMMI='COMMI_5' THEN '기타소득세' WHEN COMMI='COMMI_6' THEN '주민세' WHEN COMMI='POSI_BREAK' THEN '절사금' WHEN COMMI='RESULT' THEN '환급금' ELSE '합계' END COMMI_NM, C_TOTAL C_TOT, C_001 C_001, C_002 C_002, C_004 C_004, C_YEAR_TOT FROM (
-- 환급금 SELECT P.MEET_CD, 'RESULT' COMMI, SUM(P.WIN_AMT*P.UNIT_ODDS/100) C_TOTAL, SUM( CASE WHEN SELL_CD IN('01','03') THEN P.WIN_AMT*P.UNIT_ODDS/100 END ) C_001, SUM( CASE WHEN SELL_CD='02' THEN P.WIN_AMT*P.UNIT_ODDS/100 END ) C_002, SUM( CASE WHEN SELL_CD='04' THEN P.WIN_AMT*P.UNIT_ODDS/100 END ) C_004, SUM(S.C_YEAR_TOT)/COUNT(*) C_YEAR_TOT FROM VW_SDL_INFO V, TBJI_PC_RESULT P, ( SELECT SUM(S.WIN_AMT*S.UNIT_ODDS/100) C_YEAR_TOT FROM VW_SDL_INFO T , TBJI_PC_RESULT S WHERE T.RACE_DAY<='20091225' AND T.STND_YEAR = SUBSTR('20091225',1,4) AND T.MEET_CD= '001' AND S.MEET_CD=T.MEET_CD AND S.STND_YEAR=T.STND_YEAR AND S.TMS=T.TMS AND S.DAY_ORD=T.DAY_ORD AND INSTR(S.RLT_RUN_NO,'-') = 0 ) S WHERE V.RACE_DAY='20091225' AND V.MEET_CD= '001' AND P.MEET_CD=V.MEET_CD AND P.STND_YEAR=V.STND_YEAR AND P.TMS=V.TMS AND P.DAY_ORD=V.DAY_ORD AND INSTR(P.RLT_RUN_NO,'-') = 0 GROUP BY P.MEET_CD
)
