*     현재 총 100 컬럼임 되도록 100 컬럼 안으로 코딩을 하세요                                      *
123456789 123456789 123456789 123456789 123456789 123456789 123456789 123456789 123456789 1234567890
***************************************************************************************************/

/************************
*  공통 Script Include  *
************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";


/*********************
*  화면 변수 선언부  *
*********************/
var sSearchKind  = "";                                 //본 화면에선 사용하지않음
var sInputValue  = "";                                 //기본 검색 조건
var sCommonGubn  = "";                                 //본 화면에선 사용하지않음 

var sDivCodeType = "";
var sDivCodeGubun = "";
/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){

    if ( IsExistVar("CODE_TYPE") ) sDivCodeType = CODE_TYPE;
    if ( IsExistVar("CODE_GUBUN") ) sDivCodeGubun = CODE_GUBUN;
    if ( IsExistVar("SEARCH_VALUE") ) edNmkor.Text = SEARCH_VALUE;

	gdsPopup.ClearData();

    fcSearch();
}

/*-----------------------+
|  조회 실행 !  |
+------------------------*/
function fcSearch(obj) {	
	//필수 조회 조건 체크

	var sServiceName = "common-service:searchDivCode";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCode=dsCode ";

	fcd_AddParam("CODE_TYPE", sDivCodeType); 
	fcd_AddParam("CODE_GUBUN", sDivCodeGubun);
	fcd_AddParam("SEARCH_VALUE", edNmkor.Text);
	fcd_AddParam("DS_OUT", "dsCode");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


	grdMaster.SetFocus();
}
/*------------------------------------+
|  Transaction 후 처리 해야 할 내용!  |
+-------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
}

/*--------------+
|  확인 처리 !  |
+---------------*/
function fcChoice(obj) {
	fnc_SetReturnDs(dsCode, dsCode.GetCurrow(), gdsPopup);
    Close(true);                                                  //반환 후 조회
}

/*--------------+
|  취소 처리 !  |
+---------------*/
function fcClose(obj) {
	Close(false);     //Null 반환 후 종료
}

/*---------------------------------+
|  Enter Key Press 시 조회 실행 !  |
+----------------------------------*/
function edSearch_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam){

	if (nChar==13) fcSearch();

}

/*-----------------------------------------------+
|  그리드에서 더블 클릭 시 선택과 동일한 작업 !  |
+------------------------------------------------*/
function grdMaster_OnCellDblClick(obj,nRow,nCell,nX,nY) {

	// 배열에 해당 값을 반환 하고 종료
	fcChoice();

}

/*-----------------------------------------------+
|  그리드에서 Enter Key 시 선택과 동일한 작업 !  |
+------------------------------------------------*/
function grdMaster_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {

	// 엔터키 입력 시 배열에 해당 값을 반환 하고 종료
	if (nChar==13) fcChoice();

}


/*-------------------------------------------------------------------------------------------------+
>>  그리드 정렬
+-------------------------------------------------------------------------------------------------*/
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}
