#include "lib::rbm_app_script.js";
#include "lib::rbm_common_script.js";

function Content_OnLoadCompleted(obj)
{
    if ( isExistVar("sTarget") 
      && isExistVar("sTitle") 
      && isExistVar("sMenuId") 
      && isExistVar("sScrnId")
       ) {
        // 화면 타이틀
        div_content.Url = sTarget;
        obj.Title       = sTitle;
        stFormNm.value  = sTitle;
        stFormId.Value  = sMenuId;
        stScrnId.Value  = sScrnId;
        stMenuPath.Value= sMenuPath;
    }
}


function Content_OnActivate(obj)
{
	if(div_content.IsExistFunc("fcFormOnActivite")){

		div_content.fcFormOnActivite();
	}

	ed_msg.Border = "Flat";
	stLastLoginInfo.Border = "Flat";	
    stLastLoginInfo.text = GBL_LASTLOGININFO;


    //if(obj.GetHandle()!=G_actHandle) {

        //global.FrameBottom.fcSetActWindow(obj.GetHandle());
    //}
}


function Content_OnUnloadCompleted(obj)
{
    //global.FrameBottom.fcCloseWindow(obj.GetHandle());
}

function fcSetMsgBar(msg) {

    ed_msg.Text = msg;

    this.SetTimer(1000, 9000);
}

