    //    div_msgHLineRight.visible = false;
    //} else {
    //    div_msgHLineBottom.visible = true;
    //    div_msgHLineRight.visible = true;
    //}
	//    
	//    ed_msg.top = this.height - ed_msg.height;
	//    div_msgHLine.top = ed_msg.top - 1;
	//    trace("content.height : " + this.height + "\ned_msg.top : " + ed_msg.top + "\ndiv_msgHLine.top : " + div_msgHLine.top);

//	trace(this.width + ":" + this.height);
	/*if (div_content.IsExistFunc("fcOnSize")) {
		div_content.fcOnSize(nCx,nCy);
	}*/
	div_content.Width = obj.Width ;
	div_content.Height = obj.Height-40;
	div_content.Scroll = false;
	div_content.Scroll = true;

	ed_msg.top =  div_content.bottom + 15;
	ed_msg.width = nCx - stLastLoginInfo.width + 1;
	stLastLoginInfo.top = div_content.bottom + 15;
	stLastLoginInfo.left = nCx - stLastLoginInfo.width + 1;
}

function contents_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if (nChar == 112) fnc_OpenHelp();	// F1를 누르면 도움말 팝업 표출
	if (nChar == 113) {					// F2를 누르면 이벤트 응모표 출력 화면으로 이동
		global.FrameBottom.fcOpenMdiForm("RSM1120");
	}
}
