/************************
*  공통 Script Include  *
************************/
#include "lib::rbm_common_script.js";
#include "lib::rbm_app_script.js";


/*********************
*  화면 변수 선언부  *
*********************/
var sSearchKind     = "";                                 //본 화면에선 사용하지않음
var sInputValue     = "";                                 //기본 검색 조건
var sCommonGubn     = "";                                 //본 화면에선 사용하지않음 

var sDivCodeType    = "";
var sDivCodeGubun   = "";
var sHelpURL        = GBL_SERVER_URL+"help/";  //차후 서버 경로로 바꿔야 한다.
var tmpMenuId       = "";
/*-----------------------------------+
|  최초 화면 Load時 처리 할 사항     |
+------------------------------------*/
function fcFormLoadSetting(obj){
    tmpMenuId = GBL_MENUID;

    fcPageCall(tmpMenuId);        
}


function fcPageCall(pMenuId){

    WB.PageUrl = sHelpURL+pMenuId+".htm";
    WB.Run();
}


//닫기
function ImgConfirm_OnClick(obj,nX,nY)
{
	Close(false);
}


//인쇄
function ImgPrintOnClick(obj,nX,nY)
{
	WB.Print();
}


//이전
function ImgBeforeOnClick(obj,nX,nY)
{
    var nRow = gdsMenu.FindRow("MN_ID", tmpMenuId);

    nRow = ScrnCheck(nRow-1, -1);

    tmpMenuId = gdsMenu.GetColumn(nRow, "MN_ID");

    if(tmpMenuId != "" and tmpMenuId != null) {
        fcPageCall(tmpMenuID);
    }else{
        alert("이전 메뉴없다.");
    }
}


//다음
function ImgNext_OnClick(obj,nX,nY)
{
    var nRow = gdsMenu.FindRow("MN_ID", tmpMenuId);

    nRow = ScrnCheck(nRow+1, 1);    

    tmpMenuId = gdsMenu.GetColumn(nRow, "MN_ID");

    if(tmpMenuId != "" and tmpMenuId != null) {
        fcPageCall(tmpMenuID);
    }else{
        alert("다음 메뉴없다.");
    }


}


//SCRN_ID CHECK
function ScrnCheck(nRow, nStep)
{
    var tmpScrnId = gdsMenu.GetColumn(nRow, "SCRN_ID");

    if(tmpScrnId = "" or tmpScrnId = null) {
        return nRow+nStep;
    }else{
        return nRow;
    }
}
