//매출챠트 조회
function fcAmountSearch(){
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";

    sServiceName = "jsy0004-service:searchAmountProcess";
    sOutDataSet  = "dsSaleChart=dsSaleChart ";
    sOutDataSet += "dsSaleChart2=dsSaleChart2 ";  

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*
*   1. 오늘날짜의 회차/일차구하기
*   2. 배정현황 
*   3. 회차별 진행상태
*   4. 일차별 진행상태
*   5. 경주별 진행상태
*/
function fcProcessSearch() {

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
    var sNextDayOrd  = "";
    var sPrevTms     = "";    


    http.Sync = true;
    //==================================================1. 오늘날짜의 회차/일차구하기
    sServiceName = "jsy0004-service:searchTmsDay";
    sOutDataSet  = "dsSerchTmsDay=dsSerchTmsDay";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    //===============================================================================

    //==================================================2. 배정현황 
    sServiceName = "jsy0004-service:searchBeajung";
    sOutDataSet  = "dsBeajung=dsBeajung";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    //===============================================================================


    //==================================================3. 회차별 진행상태
    sServiceName = "jsy0004-service:searchTmsProcess";
    sOutDataSet  = "dsTmsProcess=dsTmsProcess";

    if (dsSerchTmsDay.GetColumn(0, "TMS") != "01") {
        sPrevTms = toInteger(dsSerchTmsDay.GetColumn(0, "TMS"))-1;
    }
    trace("sPrevTms >>>>>> " + sPrevTms);
    fcd_AddParam("STND_YEAR",    dsSerchTmsDay.GetColumn(0, "STND_YEAR"));
    fcd_AddParam("TMS",          dsSerchTmsDay.GetColumn(0, "TMS")); 
    fcd_AddParam("PREV_TMS",     fcLengthCheck(sPrevTms));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    //===============================================================================


    //==================================================4. 일차별 진행상태
    sServiceName = "jsy0004-service:searchDayProcess";
    sOutDataSet  = "dsDayProcess=dsDayProcess";

    if (fnc_IsNull(dsSerchTmsDay.GetColumn(0, "DAY_ORD"))) {
        sNextDayOrd = "1";
    }else{
        if (dsSerchTmsDay.GetColumn(0, "DAY_ORD") == dsSerchTmsDay.GetColumn(0, "MAXDAY")) {
            sNextDayOrd = dsSerchTmsDay.GetColumn(0, "DAY_ORD");
        }else{
            sNextDayOrd = toInteger(dsSerchTmsDay.GetColumn(0, "DAY_ORD"))+1;
        }
    }

    fcd_AddParam("STND_YEAR",    dsSerchTmsDay.GetColumn(0, "STND_YEAR"));
    fcd_AddParam("TMS",          dsSerchTmsDay.GetColumn(0, "TMS"));
    fcd_AddParam("DAY_ORD",      sNextDayOrd);
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    //===============================================================================


    //==================================================5. 경주별 진행상태
    sServiceName = "jsy0004-service:searchRaceProcess";
    sOutDataSet  = "dsRaceProcess=dsRaceProcess";

    fcd_AddParam("STND_YEAR",    dsSerchTmsDay.GetColumn(0, "STND_YEAR"));
    fcd_AddParam("TMS",          dsSerchTmsDay.GetColumn(0, "TMS"));
    fcd_AddParam("DAY_ORD",      dsSerchTmsDay.GetColumn(0, "DAY_ORD"));
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    //===============================================================================
    http.Sync = false;

}


function fcLengthCheck(pVal) {
    if (pVal.length != 2) {
        pVal = "0"+pVal;
    }
    trace("fcLengthCheck   >>>> " + pVal);
    return pVal;
}


function fcSetRaceProcess() {

    trace("TMS >>>>>>>>>>>>>> " + dsSerchTmsDay.GetColumn(0, "TMS"));
    if (!fnc_IsNull(dsSerchTmsDay.GetColumn(0, "TMS"))) {
        txtTms.Text = dsSerchTmsDay.GetColumn(0, "TMS")+"회차";
        txtRaceNM.text = dsSerchTmsDay.GetColumn(0, "SPEC_RACE_NM");
    }


    //회차별 진행현황
    if (dsTmsProcess.GetColumn(0, "IPSO_CNT") == "1") {
        Image12.Visible = true;
        Image10.Visible = false;
    }else{
        Image12.Visible = false;
        Image10.Visible = true;
    }
    if (dsTmsProcess.GetColumn(0, "GUMCH_CNT") == "1") {
        Image16.Visible = true;
        Image15.Visible = false;
    }else{
        Image16.Visible = false;
        Image15.Visible = true;
    }
    if (dsTmsProcess.GetColumn(0, "TMSEND_CNT") == "1") {
        Image24.Visible = true;
        Image23.Visible = false;
    }else{
        Image24.Visible = false;
        Image23.Visible = true;
    }
    if (dsTmsProcess.GetColumn(0, "SANGGUM_CNT") == "1") {
        Image26.Visible = true;
        Image25.Visible = false;
    }else{
        Image26.Visible = false;
        Image25.Visible = true;
    }

    //일차별 진행현황
    if (!fnc_IsNull(dsSerchTmsDay.GetColumn(0, "TMS"))) {
        if (dsSerchTmsDay.GetColumn(0, "DAY_ORD") == "" || dsSerchTmsDay.GetColumn(0, "DAY_ORD") == null) {
            txtDayOrd.Text = dsSerchTmsDay.GetColumn(0, "DAY_ORD")+"1일차";
        }else{
            if (dsSerchTmsDay.GetColumn(0, "DAY_ORD") == dsSerchTmsDay.GetColumn(0, "MAXDAY")) {
                txtDayOrd.Text = dsSerchTmsDay.GetColumn(0, "DAY_ORD")+"일차";
            }else{
                txtDayOrd.Text = toInteger(dsSerchTmsDay.GetColumn(0, "DAY_ORD"))+1+"일차";
            }

            txtRaceState.Text  = dsSerchTmsDay.GetColumn(0, "TMS")+"회차 "+dsSerchTmsDay.GetColumn(0, "DAY_ORD")+"일차 경주";
        }
    }

    //일차별 진행현황
    if (dsDayProcess.GetColumn(0, "NOCONFIRM_CNT") == "1") {
        Image18.Visible = true;
        Image17.Visible = false;
    }else{
        Image18.Visible = false;
        Image17.Visible = true;
    }
    if (dsDayProcess.GetColumn(0, "GIABASU_CNT") == "1") {
        Image20.Visible = true;
        Image19.Visible = false;
    }else{
        Image20.Visible = false;
        Image19.Visible = true;
    }
    if (dsDayProcess.GetColumn(0, "CONFIRM_CNT") == "1") {
        Image22.Visible = true;
        Image21.Visible = false;
    }else{
        Image22.Visible = false;
        Image21.Visible = true;
    }


    //경주별 진행현황
    if (!fnc_IsNull(dsSerchTmsDay.GetColumn(0, "DAY_ORD"))) {
        txtRankConfirm.Text = dsRaceProcess.GetColumn(0, "RACECONFIRM");
        txtRaceReady.Text   = dsRaceProcess.GetColumn(0, "RACECNT");
    }else{
        txtRankConfirm.Text = "휴";
        txtRaceReady.Text   = "장";
    }

}

