/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
//		fcd_AddCodeRow(dsOutRacerDgre, "전체", "ALL");
//		fcSearch();
	}

    if ( sServiceName == "jsy0004-service:searchBoardList" ) {
        fcSetPageCombo();
    }

    if ( sServiceName == "jsy0004-service:searchRaceProcess" ) {
        fcSetRaceProcess();
    }

    if ( sServiceName == "jsy0004-service:searchAmountProcess" ) {
        fcSetAmountChart();
    }


	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


