/*-------------------------------------------------------------------------------------------------+
>>  게시글 더블클릭시
+-------------------------------------------------------------------------------------------------*/
function grd_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var seqNo       = "";
    var sClsCd      = "";
    if(obj.id == "grdNotice") {
        seqNo   = dsOutNotice.getColumn(nRow, "SEQ_NO");
        sClsCd  = sNOTICECLSCD;        
    } else if(obj.id == "grdQna") {
        seqNo   = dsOutQna.getColumn(nRow, "SEQ_NO");
        sClsCd  = sQNACLSCD;
    }

	if ( fnc_IsNull(seqNo) ) return;

    // Content로 넘겨지는 arguements
	var sArg    = "Arg_seqNo="  + quote(seqNo);
        sArg   += " Arg_clsCd=" + quote(sClsCd);

    if ( Dialog("frm_jsy::JSY0005.xml", sArg, 250, 250, "") ) fcSearch(qnaPageCombo.Value, sClsCd);
}


