/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       =  this.getform().id;
var sBUTTONLIST   = 'TTTTTT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                         // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                          // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";


var sSendUser 	  = "";
var sRecvUser  	  = "";
var sTitle  	  = "";
var sLinkUrl 	  = "";
var sDesc  		  = "";
var sCompId       = "";
var sLinkMenuId   = "";

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) { 


	dsSendMsg.ClearData();
	var nRow = dsSendMsg.AddRow();

    dsSendMsg.SetColumn(nRow,"SEND_USER",sSendUser);		// 수신자 사번
    dsSendMsg.SetColumn(nRow,"RECV_USER",sRecvUser);		// 송신자 사번
    dsSendMsg.SetColumn(nRow,"TITLE",sTitle);				// 제목
    dsSendMsg.SetColumn(nRow,"LINK_URL",sLinkUrl);  		// 링크
    dsSendMsg.SetColumn(nRow,"DESC",sDesc);					// 내용 
    dsSendMsg.SetColumn(nRow,"COMP_ID",sCompId);			// 


	taDesc.SetFocus();


	fcSearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {

}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

}



/*-------------------------------------------------------------------------------------------------+
>>  조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	 sServiceName = "common-service:searchAlarmUser";
	 sInDataSet   = "";
	 sOutDataSet  = "dsRecvUser=dsRecvUser";

	 //조회조건 : 
	 fcd_AddParam("USER_IDS", replace(sRecvUser,",","','") );


	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/



/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 필수항목체크
	oDataSet          = dsSendMsg;
	sMandatoryColID   = "DESC";
	sMandatoryColName = "내용";
	oGrid             = grdSendMsg;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if(fnc_IsNull(dsSendMsg.GetColumn(0,"TITLE"))) {
        fnc_Message(SNIS_COM_0001, "SERVERMSG", "내용");
        return;
    }

        if(fnc_IsNull(dsSendMsg.GetColumn(0,"DESC"))) {
        fnc_Message(SNIS_COM_0001, "SERVERMSG", "내용");
        return;
    }


   var sReceiveId ="";

   for(var i = 0; i < dsRecvUser.GetRowCount();i++){

		if(dsRecvUser.GetColumn(i,"CHK") == "1"){

			sReceiveId += dsRecvUser.GetColumn(i,"USER_ID")+",";

		}
   }

   if(Length(sReceiveId) >0){

	   sReceiveId =sReceiveId.SubStr(0,Length(sReceiveId)-1);

	   fcd_SendMessage(quote(dsSendMsg.GetColumn(0,"SEND_USER"))	// 송신자 사번 
					, sReceiveId	// 수신자 사번 
					, dsSendMsg.GetColumn(0,"TITLE")	    // 제목
					, sLinkMenuId 	// 수신자 번호
					, dsSendMsg.GetColumn(0,"DESC")			// 내용
					, "comSendMsg"
					);

	   fnc_Message(L_COM_ALT_0011,"N","쪽지  전송");


	   fcEnd();			
   }

}




/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}




/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


}
