/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

     sBUTTONLIST   = 'FFFFFFT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	stFormTitle2.Text = sTitle;



	calEndDate.Value = Today();


	//게시판 ID 설정
	switch ( sMenuId ) {
		 case "RBB1010":	//공지사항
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010831";
		 break;
		 case "RBB1020":	//경륜경정게시판
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010832";
		 break;
		 case "RBB1030":	//회의자료 
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010833";
		 break;
		 case "RBB1040":	//신문기사
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010834";
		 break;
		 case "RBB1050":	//지점게시판
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010835";
		 break;
		 case "RBB1060":	//이벤트
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010836";
		 break;
		 case "RBB1070":	//사업지식
			calStartDate.Value = (AddDate(Today(), -30));	// 현재달의 첫날(매월 1일)
			sBbsId= "B0010837";
		 break;
    }

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	fcSearch();
}


/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsBusiNotice.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	// 전자결재 DB 장애 여부 체크 : 장애시 조회 안함
	var iCnt = 0;
	iCnt = gdsDbStatus.FindRow("CD","JUPITER");
	if (iCnt >= 0) {
		if (gdsDbStatus.GetColumn(iCnt, "CD_NM") == "1") return;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbb1010-service:searchBusiNotice";
	var sInDataSet   = "";
	var sOutDataSet  = "dsBusiNotice=dsBusiNotice ";

	fcd_AddParam("BBS_ID", sBbsId);

	fcd_AddParam("SRCH_STR_DT", calStartDate.Value);
	fcd_AddParam("SRCH_END_DT", calEndDate.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbb1010-service:searchBusiNotice" ) {

	}

}



function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

		var params="";
		params+="sDocYearMON="+dsBusiNotice.GetColumn(nRow,"DOC_YEARMON");
		params+=" sDocNumber="+dsBusiNotice.GetColumn(nRow,"DOC_NUMBER");
		params+=" sBbsId="+dsBusiNotice.GetColumn(nRow,"BBS_ID");

		Dialog("frm_rbb::RBB1011.xml", params, "862", "654",  "Scroll=true,titlebar=true,Resize=true,Autosize=false", 10, 10);
}

function fcGrdOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
		if(Length(dsBusiNotice.GetColumn(nRow,"BBS_ID")) <=0){
			return;
		}

		var params="";
		params+="sDocYearMON="+dsBusiNotice.GetColumn(nRow,"DOC_YEARMON");
		params+=" sDocNumber="+dsBusiNotice.GetColumn(nRow,"DOC_NUMBER");
		params+=" sBbsId="+dsBusiNotice.GetColumn(nRow,"BBS_ID");

		Dialog("frm_rbb::RBB1011.xml", params, "862", "654",  "Scroll=true,titlebar=true,Resize=true,Autosize=false", 170, 40);
}

function fcEnd(obj) {

	// 화면 종료
	Close();
}

function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsBusiNotice";
	if (   !fnc_FormUnloadCheck(sDataset  , sMenuId) ) return false;
}
