function fcDelRow(obj) {

	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			//¡¬ºÆ disable º≥¡§ 

			dsAppoSeat.SetColumn(dsAppoSeat.FindRowNF("AREA_SEAT_NO",dsSalesMana.GetColumn(dsSalesMana.row,"AREA")+"_"
									+dsSalesMana.GetColumn(dsSalesMana.row,"SEAT_NO")), "APPO_CNT", "0");

			dsSalesMana.DeleteRow(dsSalesMana.row);

			setTotalSaveAmt();

			break;

	}
}


