/*-------------------------------------------------------------------------------------------------+
>>  Transaction »ƒ √≥∏Æ «ÿæﬂ «“ ≥ªøÎ!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {


	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if (sServiceName == "common-service:searchCode") {
		if (dsCustKd.rowcount() > 0) {
			var nRow = dsCustKd.SearchRow("CD_TERM3='Y'");
			if (nRow != -1) sDefaultCustKind = dsCustKd.GetColumn(nRow, "CD");
		}
	}
	// ¿œ¿œ∏≈«• ¡∂»∏ øœ∑· »ƒ
	if ( sServiceName == "rbo2010-service:searchAppoSeatMana" ) {

			//edTotSaveAmt.Value = dsAppoSeatMana.Sum("SAVE_AMT");
			//edTotAppoCnt.Value = dsAppoSeatMana.GetRowCount();

			var totSaveAmt = 0;
			var totSaveCnt = 0;

			for ( var i = 0; i < dsAppoSeatMana.GetRowCount(); i++ ) {
				if (dsAppoSeatMana.GetColumn(i,"REFUND_YN") != "»Ø∫“") {
					totSaveAmt += dsAppoSeatMana.GetColumn(i,"SAVE_AMT");
					totSaveCnt++;
				}
			}

			edTotSaveAmt.Value = totSaveAmt;
			edTotAppoCnt.Value = totSaveCnt;


			edRealSaveAmt.Text = 0;
			stSaveAmt.Text =  0;

	}


	// ∆«∏≈∏Ò∑œ ¡∂»∏ øœ∑· »ƒ
	if ( sServiceName == "rbo2010-service:searchSalesMana" ) {

			stSaveAmt.Text = dsSalesMana.Sum("SAVE_AMT");
			edRealSaveAmt.Text = dsSalesMana.Sum("SAVE_AMT");

			sAppoGridSel = true;
	}


	// ¡¬ºÆ¡§∫∏  ¡∂»∏ øœ∑· »ƒ
	if ( sServiceName == "rbo2010-service:searchAppoSeat" ) {

			setAppoSeat();
	}





	// ¿œ¿œ∏≈«• ¿˙¿Â øœ∑· »ƒ
	if ( sServiceName == "rbo2010-service:saveAppoSeatPrint" ) {

			 fcAppoSeatPrint();

			 dsSalesMana.ClearData();
			 sAppoGridSel = false;



			 fcSearch();
			 setAppoSeat();

			 //ªÁøÎ¿⁄ »Æ¿Œ√¢ ¡¬ºÆ¡§∫∏ æ˜µ•¿Ã∆Æ 
			 var objPopup =getPopupForm();
			 if(objPopup != null){
				objPopup.fcSearchSeat();
			 }

	}


		// ¿œ¿œ∏≈«• ¿˙¿Â øœ∑· »ƒ
	if ( sServiceName == "rbo2010-service:saveAppoSeatMana" ) {


			 dsSalesMana.ClearData();
			 sAppoGridSel = false;



			 fcSearch();
			 setAppoSeat();

			 //ªÁøÎ¿⁄ »Æ¿Œ√¢ ¡¬ºÆ¡§∫∏ æ˜µ•¿Ã∆Æ 
			 var objPopup =getPopupForm();
			 if(objPopup != null){
				objPopup.fcSearchSeat();
			 }

	}



	// ¡¬ºÆ¡§∫∏  ¿˙¿Â§∑ øœ∑· »ƒ
	if ( sServiceName == "rbo2010-service:saveSeatGbn" ) {

			fcSearchSeat();
	}

}



