/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo2010-service:searchAppoSeatMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsAppoSeatMana=dsAppoSeatMana ";


	fcSearchSeat();

	fcd_AddParam("SALES_DT", calSalesDt.Value);
	fcd_AddParam("BRNC_CD", stBrncCd.Text);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


