}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSalesMana) && !fnc_DatasetChangeCheck(dsAppoSeatMana) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsSalesMana;
	sMandatoryColID   = "AREA|SEAT_NO|UNIT_PRICE|CUST_KD_CD|SAVE_AMT";
	sMandatoryColName = "구역|좌석번호|단가|고객구분|입금액";
	oGrid             = grdSalesMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";



	sServiceName = "rbo2010-service:saveAppoSeatMana";
	sInDataSet   = "dsSalesMana=dsSalesMana:u ";

	sOutDataSet  = "dsSalesMana=dsSalesMana ";

	// jobGbn => 01:입력/수정, 02:삭제 , 03: 환불처리 

    switch ( obj.id ) {
		case "btnSave" :
			break;
		case "btn_sellsaveprint" :	// 저장 + 발권 
			if ( !fcSaveCheck() ) return;

			sServiceName = "rbo2010-service:saveAppoSeatPrint";

			fcd_AddParam("jobGbn", "01");

			break;

		case "btn_sellsave" :		// 저장 
			if ( !fcSaveCheck() ) return;

			sInDataSet   += "dsAppoSeatMana=dsAppoSeatMana:u ";

			fcd_AddParam("jobGbn", "01");

			break;

		case "btn_sellprint" :		// 발권 
			//발권 처리 추가해야함 
			fcAppoSeatPrint();
			return;

			break;
		case "btn_selldel" :		// 삭제 

			fcd_AddParam("jobGbn", "02");
			sInDataSet   = "dsSalesMana=dsSalesMana ";

			break;

		case "btn_sellretn" :		// 환불
			fcd_AddParam("jobGbn", "03");
			sInDataSet   = "dsSalesMana=dsSalesMana ";

			break;


	}



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcAppoSeatPrint(){

			if(dsSalesMana.GetRowCount() <= 0){
				return;
			}else{
				if(dsSalesMana.GetColumn(0,"SALES_NO") == null ||dsSalesMana.GetColumn(0,"SALES_NO") =="" ){
					fnc_Message(SNIS_RBM_B010,"N","");
					return;
				}

			}

			sSubUrl   = "";                                             // 레포트 파일이 있는 서브디렉토리
			sRptNm    = "rbo/rbo2010.jrf";                                // 레포트 파일명
			sDataSet  = "dsSalesMana=dsSalesMana ";							//UBI 쿼리명, MIP Dataset명"
			sArg      = "";		
			sRptDrct  = 0;                                              // 출력방향(0 : 수직, 1 : 수평)                                      // 출력방향(0 : 수직, 1 : 수평)

			fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}


