/*-------------------------------------------------------------------------------------------------+
>> 좌석번튼 클릭  이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/

function fcSeatBtnOnClick(obj)
{


			var aObjStr = Split(Obj.id,"_");    // 버튼 ID : btn + _ + area + _ + seatNo
			var sArea = aObjStr[1];				// 구역
			var sSeat = aObjStr[2];				// 좌석 
			var sAreaSeat = sArea+"_"+sSeat;	// 구역 + 좌석 

			var sSeatGbn   = dsSeatNo.GetColumn(dsSeatNo.FindRowNF("CD",sAreaSeat), "CD_NM"); 			// 좌석구분 
			var nUnitPrice = dsSeatGbn.GetColumn(dsSeatGbn.FindRowNF("CD",sSeatGbn) , "CD_TERM1");	 	// 단가
			var sSeatSte   = dsAppoSeat.GetColumn(dsAppoSeat.FindRowNF("AREA_SEAT_NO",sAreaSeat), "SALE_SEAT_GBN"); // 좌석구분 

			var sAppoCnt   = dsAppoSeat.GetColumn(dsAppoSeat.FindRowNF("AREA_SEAT_NO",sAreaSeat), "APPO_CNT"); // 좌석구분 

			//수리중 일 경우 선택 안됨 
			if(sSeatSte == sSeatRepCd){
				return;
			}

			//매표된좌석일 경우  선택안됨 
			if(sAppoCnt > "0"){
				return;
			}

			if(sAppoGridSel){
				dsSalesMana.ClearData();
				sAppoGridSel = false;
			}

			nRow = dsSalesMana.AddRow(); // Record Add




			dsSalesMana.SetColumn(nRow,"SALES_DT",calSalesDt.Value);	//판매일자
			dsSalesMana.SetColumn(nRow,"BRNC_CD",stBrncCd.Text);		//지점번호
			dsSalesMana.SetColumn(nRow,"AREA",sArea);					//구역
			dsSalesMana.SetColumn(nRow,"SEAT_NO",sSeat);				//좌석번호
			dsSalesMana.SetColumn(nRow,"UNIT_PRICE",nUnitPrice);		//단가 
			dsSalesMana.SetColumn(nRow,"SAVE_AMT",nUnitPrice);			//입금액  
			dsSalesMana.SetColumn(nRow,"CUST_KD_CD","P3");			//고객구분 



			grdSalesMana.SetCellPos(5);
			grdSalesMana.SetFocus();

			//좌석 disable 설정 
			dsAppoSeat.SetColumn(dsAppoSeat.FindRowNF("AREA_SEAT_NO",sAreaSeat), "APPO_CNT", "1");

}



/*-------------------------------------------------------------------------------------------------+
>> 좌석 지정 가능여부 설정 
+-------------------------------------------------------------------------------------------------*/
function setAppoSeat(){

		var tmpSeatObj ;

		for(var i = 0 ; i < dsAppoSeat.GetRowCount() ; i++){  
			tmpSeatObj = Find("btn_" + dsAppoSeat.GetColumn(i,"AREA_SEAT_NO"));  // 구역 + 좌석번호 

			if(tmpSeatObj != null){
				if(dsAppoSeat.GetColumn(i,"APPO_CNT") > "0"){  // 지정된좌석잇으면 버튼 비활성화 

					//tmpSeatObj.Enable = false;

					tmpSeatObj.Bkcolor = "User5";
					tmpSeatObj.color = "gray";


				}else{
					if(dsAppoSeat.GetColumn(i,"SALE_SEAT_GBN") == sSeatGnrCd){
						tmpSeatObj.Enable = true;
						//tmpSeatObj.Bkcolor = "User0";
						//tmpSeatObj.color = "User5";
						tmpSeatObj.Text = dsAppoSeat.GetColumn(i,"SEAT_NO");
					}else{

						tmpSeatObj.Enable = false;
						//tmpSeatObj.Bkcolor = "User5";
						//tmpSeatObj.color = "gray";
						tmpSeatObj.Text = dsAppoSeat.GetColumn(i,"SALE_SEAT_GBN_NM");

					}
				}
			}
		}
}


/*-------------------------------------------------------------------------------------------------+
>> 좌석 지정 가능여부 설정 
+-------------------------------------------------------------------------------------------------*/
function setAppoSeatByAreaSeatNo(sAreaSeatNo){

		var tmpSeatObj ;

		tmpSeatObj = Find("btn_" + sAreaSeatNo);

		if(tmpSeatObj != null){
			if(dsAppoSeat.GetColumn(dsAppoSeat.FindRowAs("AREA_SEAT_NO", sAreaSeatNo) ,"APPO_CNT") > "0"){

				tmpSeatObj.Enable = false;

				//tmpSeatObj.Bkcolor = "User5";
				//tmpSeatObj.color = "gray";




			}else{

				 tmpSeatObj.Enable = true;
				//tmpSeatObj.Bkcolor = "User0";
				//tmpSeatObj.color = "User5";



			}
		}

}


/*-------------------------------------------------------------------------------------------------+
>> dataset columnchange 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	   switch ( obj.id ) {
			case "dsAppoSeat" :	// 좌석정보 
				if(strColumnID == "APPO_CNT"){
					setAppoSeatByAreaSeatNo(obj.getColumn(nRow, "AREA_SEAT_NO") );
				}

				break;

			case "dsSalesMana" :	// 좌석정보 
				var nCustRow;
				var sDcYn;
				var sDcRate;
				if(strColumnID == "CUST_KD_CD"){
						var nCustRow = dsCustKd.FindRowNF("CD",varNewValue);
						var sDcYn	 = dsCustKd.GetColumn(nCustRow,"CD_TERM1");	// 과금여부
						var sDcRate  = ToInteger(dsCustKd.GetColumn(nCustRow,"CD_TERM2")); // 할인율 
						var nUnitPrice = ToInteger(dsSalesMana.GetColumn(nRow,"UNIT_PRICE"));	//단가 

						if(sDcYn == "Y"){
							if(sDcRate > 0){
								dsSalesMana.SetColumn(nRow,"SAVE_AMT",nUnitPrice - ROUND( nUnitPrice * sDcRate * 0.01 ));
							}else{
								dsSalesMana.SetColumn(nRow,"SAVE_AMT",nUnitPrice);
							}


						}else if(sDcYn == "N"){ // 무료 

							dsSalesMana.SetColumn(nRow,"SAVE_AMT",0);
						}

				}

				if(strColumnID == "SAVE_AMT"){
					setTotalSaveAmt();
				}

				break;

		}

}


/*-------------------------------------------------------------------------------------------------+
>> Grid  OnCellDblClick 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
		   switch ( obj.id ) {
				case "grdAppoSeatMana" :	// 좌석정보 
					fcSearchSales(dsAppoSeatMana.GetColumn(nRow,"SALES_NO"));
					break;
			}
}



function setTotalSaveAmt(){
			stSaveAmt.Text = dsSalesMana.Sum("SAVE_AMT");
			edRealSaveAmt.Text = dsSalesMana.Sum("SAVE_AMT");
}




/*-------------------------------------------------------------------------------------------------+
>> button click 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcBtnOnClick(obj)
{		   
			switch ( obj.id ) {
				case "btn_popupopen" :	// 좌석정보 
						var sUrl 	= "";
						var sArgs 		= "";
						var arrReturn   = array(2);

						sUrl = "frm_rbo::RBO2011.xml";
						sArgs = "sSalesDt="+quote(calSalesDt.Value)+" sBrncCd="+quote(stBrncCd.Text) ;	


						Open(sUrl, sArgs, 1020,600, "Title=true CloseFlag=true","1","1");


					break;

				case "btn_popupclose" :	// 좌석정보 

					for ( var i = 0; i < global.windows.count; i++ ) {
						if ( global.windows[i].id == "RBO2011" ) {
							global.windows[i].close();

						}
					}
					break;
			}

}



/*-------------------------------------------------------------------------------------------------+
>> 팝업창 OBJ  SEARCH 
+-------------------------------------------------------------------------------------------------*/

function getPopupForm(){

		for ( var i = 0; i < global.windows.count; i++ ) {
			if ( global.windows[i].id == "RBO2011" ) {
				return global.windows[i];

			}
		}

		return;
}




function fcCalOnChange(obj,strOldText,strNewText)
{
		switch ( obj.id ) {
				case "calSalesDt" :
					if(strOldText <> strNewText &&  Length(strNewText) > 0){
						fcSearch();	
					}

					break;

	   }
}



function fcSeatBtnOnRButtonUP(obj,nPosX,nPosY)
{	


		var aObjStr = Split(Obj.id,"_");    // 버튼 ID : btn + _ + area + _ + seatNo
		var sArea = aObjStr[1];				// 구역
		var sSeat = aObjStr[2];				// 좌석 
		var sAreaSeat = sArea+"_"+sSeat;	// 구역 + 좌석 

		var sSeatGbn   = dsAppoSeat.GetColumn(dsAppoSeat.FindRowNF("AREA_SEAT_NO",sAreaSeat), "SALE_SEAT_GBN"); // 좌석구분 
		var sAppoCnt   = dsAppoSeat.GetColumn(dsAppoSeat.FindRowNF("AREA_SEAT_NO",sAreaSeat), "APPO_CNT"); // 좌석구분 
		var sUpSeatGbn ="";

		if(sSeatGbn == sSeatGnrCd){
			if(sAppoCnt>"0"){
				fnc_Message(SNIS_RBM_B004,"N");

				return;
			}

			if(!fnc_Message(SNIS_RBM_B003,"Y","수리")){
				return;
			}

			sUpSeatGbn = sSeatRepCd;

		}else if(sSeatGbn == sSeatRepCd){		
			if(!fnc_Message(SNIS_RBM_B003,"Y","사용")){
				return;
			}

			sUpSeatGbn = sSeatGnrCd;
		}else{		
			return;
		}


		//솨석정보저장 	
		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";

		//초기화 
		dsSeatGbnCd.ClearData();
		var nRow = dsSeatGbnCd.AddRow();

		//설정
		dsSeatGbnCd.SetColumn(0,"GRP_CD","039");
		dsSeatGbnCd.SetColumn(0,"CD",sAreaSeat);
		dsSeatGbnCd.SetColumn(0,"CD_TERM2",sUpSeatGbn);		


		sServiceName = "rbo2010-service:saveSeatGbn";
		sInDataSet   = "dsSeatGbnCd=dsSeatGbnCd:a ";

		fcd_AddParam("jobGbn", "04");


		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}
