function fcInit(obj) {

	dsSalesMana.ClearData();

}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	if(calSalesDt.Value == null || calSalesDt.Value == ""){
		fnc_Message(SNIS_COM_1035,"N","판매일자");
		return;
	}



	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo2020-service:searchAppoSeatMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsAppoSeatMana=dsAppoSeatMana ";


	fcSearchSeat();

	fcd_AddParam("SALES_DT", calSalesDt.Value);
	fcd_AddParam("BRNC_CD", stBrncCd.Text);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  좌석정보 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchSeat() {

	var sServiceName = "rbo2020-service:searchAppoSeat";
	var sInDataSet   = "";
	var sOutDataSet  = "dsAppoSeat=dsAppoSeat ";


	fcd_AddParam("SALES_DT", calSalesDt.Value);
	fcd_AddParam("BRNC_CD", stBrncCd.Text);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



/*-------------------------------------------------------------------------------------------------+
>>  판매번호별 정보 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchSales(sSaleNo){
	var sServiceName = "rbo2020-service:searchSalesMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsSalesMana=dsSalesMana ";


	fcd_AddParam("SALES_DT", calSalesDt.Value);
	fcd_AddParam("BRNC_CD", stBrncCd.Text);
	fcd_AddParam("SALES_NO", sSaleNo);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSalesMana) && !fnc_DatasetChangeCheck(dsAppoSeatMana) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	if(calSalesDt.Value == null || calSalesDt.Value == ""){
		fnc_Message(SNIS_COM_1035,"N","판매일자");
		return;
	}


	// 필수항목체크
	oDataSet          = dsSalesMana;
	sMandatoryColID   = "AREA|SEAT_NO|GRN_CRD_NO";
	sMandatoryColName = "구역|좌석번호|그린카드번호";
	oGrid             = grdSalesMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	for(var i = 0 ;i<dsSalesMana.GetRowCount();i++){
		if( Length(dsSalesMana.GetColumn(i,"GRN_CRD_NO")) <> 5) {
			 	fnc_Message(SNIS_RBM_B007,"N","그린카드번호","5");
			 	return false; 

		}    
	}


	return true;
}


var sAddInDataSet = "";
var sSaveServiceName = "";
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

    sAddInDataSet = "";

	sServiceName = "rbo2020-service:saveAppoSeatMana";
	sSaveServiceName = "rbo2020-service:saveAppoSeatMana";


	sInDataSet   = "dsSalesMana=dsSalesMana:u ";

	sOutDataSet  = "dsSalesMana=dsSalesMana ";

	// jobGbn => 01:입력/수정, 02:삭제 , 03: 환불처리 

    switch ( obj.id ) {
		case "btnSave" :
			if ( !fcSaveCheck() ) return;

			fcd_AddParam("jobGbn", "01");

			fcSearchAppoSeatCard();

			break;
		case "btn_sellsaveprint" :	// 저장 + 발권 
			if ( !fcSaveCheck() ) return;

			sSaveServiceName = "rbo2020-service:saveAppoSeatPrint";

			fcd_AddParam("jobGbn", "01");

			fcSearchAppoSeatCard();

			break;

		case "btn_sellsave" :		// 저장 
			if ( !fcSaveCheck() ) return;

			sAddInDataSet   = "dsAppoSeatMana=dsAppoSeatMana:u ";

			fcd_AddParam("jobGbn", "01");

			fcSearchAppoSeatCard();

			break;

		case "btn_sellprint" :		// 발권 
			//발권 처리 추가해야함 
			fcAppoSeatPrint();
			return;

			break;
		case "btn_selldel" :		// 삭제 

			fcd_AddParam("jobGbn", "02");
			sInDataSet   = "dsSalesMana=dsSalesMana ";

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

			break;

	}


}



