/*-------------------------------------------------------------------------------------------------+
>>  매표 정보 저장 전 카드등급 체크 후 일반일 경우 저장한다. 
+-------------------------------------------------------------------------------------------------*/

function fcSaveSeatCardCheck(){
		//!!!! 고객등급 체크 
		var sCustGrd;
		var nCardRow;
		var sCustGrdNm;
		for(var i = 0 ;i<dsSalesMana.GetRowCount();i++){
			nCardRow= dsAppoSeatCard.FindRowNF("GRN_CRD_NO",dsSalesMana.GetColumn(i,"GRN_CRD_NO"));
			sCustGrd = dsAppoSeatCard.GetColumn( nCardRow,"CUST_GRD_CD");
			sCustGrdNm = dsCustGrd.LookUp("CD",sCustGrd,"CD_NM");

			if(nCardRow>=0 && sCustGrd <> "001"){
				 if(!fnc_Message(SNIS_RBM_B005,"Y",sCustGrdNm)){
					return false; 
				 }

			}    
		}

		return true;

}


