	var sInDataSet   = "";
	var sOutDataSet  = "";




	sInDataSet   = "dsSalesMana=dsSalesMana:u ";

	sOutDataSet  = "dsSalesMana=dsSalesMana ";

	sInDataSet  += sAddInDataSet;


	fcd_AddParam("jobGbn", "01");

    fcd_SendTransaction(sSaveServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


