/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 일일매표 조회 완료 후
	if ( sServiceName == "rbo2020-service:searchAppoSeatMana" ) {

			edTotAppoCnt.Value = dsAppoSeatMana.GetRowCount();
	}


	// 판매목록 조회 완료 후
	if ( sServiceName == "rbo2020-service:searchSalesMana" ) {

			sAppoGridSel = true;
	}


	// 좌석정보  조회 완료 후
	if ( sServiceName == "rbo2020-service:searchAppoSeat" ) {

			setAppoSeat();
	}



  // 일일매표 저장 완료 후
	if ( sServiceName == "rbo2020-service:saveAppoSeatPrint" ) {

			 fcAppoSeatPrint();

			 //dsSalesMana.ClearData();
			 sAppoGridSel = false;



			 fcSearch();
			 setAppoSeat();

			 //사용자 확인창 좌석정보 업데이트 
			 var objPopup =getPopupForm();
			 if(objPopup != null){
				objPopup.fcSearchSeat();
			 }

	}



	// 일일매표 저장 완료 후
	if ( sServiceName == "rbo2020-service:saveAppoSeatMana" ) {

			 dsSalesMana.ClearData();
			 sAppoGridSel = false;



			 fcSearch();
			 setAppoSeat();

			 //사용자 확인창 좌석정보 업데이트 
			 var objPopup =getPopupForm();
			 if(objPopup != null){
				objPopup.fcSearchSeat();
			 }

	}



	// 좌석정보  저장 완료 후
	if ( sServiceName == "rbo2020-service:saveSeatGbn" ) {

			fcSearchSeat();
	}

	// 고객카드번호 
	if ( sServiceName == "rbo2020-service:searchAppoSeatCard" ) {

		 if(fcSaveSeatCardCheck()){

			fcSaveSeat();
		 }

	}


}



