/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsRollBrncMove.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo3020-service:searchRollBrncMove";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollBrncMove=dsRollBrncMove ";
		//sOutDataSet += "dsIpMana=dsIpMana ";

	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);
	fcd_AddParam("OUT_BRNC_CD", cboOutBrncCd.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  기준년도 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchStndYear() {

	var sServiceName = "rbo3020-service:searchStndYear";
	var sInDataSet   = "";
	var sOutDataSet  = "dsStrYear=dsStrYear ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRollBrncMove) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsRollBrncMove;
	sMandatoryColID   = "ROLL_GBN|IN_BRNC_CD|MOVE_CNT";
	sMandatoryColName = "용지구분|입고지점|이동수량";
	oGrid             = grdRollBrncMove;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {    
	if ( !fcSaveCheck() ) return;


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbo3020-service:saveRollBrncMove";
	sInDataSet   = "dsRollBrncMove=dsRollBrncMove:u ";

	sOutDataSet  = "dsRollBrncMove=dsRollBrncMove ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( fnc_DatasetChangeCheck(dsRollBrncMove) ) {

			}

			nRow = dsRollBrncMove.AddRow(); // Record Add

			dsRollBrncMove.SetColumn(nRow,"STND_YEAR",cboStndYear.Value );
			dsRollBrncMove.SetColumn(nRow,"OUT_BRNC_CD",cboOutBrncCd.Value );
			dsRollBrncMove.SetColumn(nRow,"TMS",cboTms.Value );

			dsRollBrncMove.SetColumn(nRow,"INST_DT",fnc_DateFormat(today(),"-") );

			grdRollBrncMove.SetCellPos(2);
			grdRollBrncMove.SetFocus(); // 입력 첫 항목으로 Focus

			break;

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
