/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 용지재고 조회 완료 후
	if ( sServiceName == "rbo3020-service:searchRollBrncMove" ) {

	}


	// 용지재고 저장 완료 후
	if ( sServiceName == "rbo3020-service:saveRollBrncMove" ) {
		fcSearch();

	}

	// 용지재고 저장 완료 후
	if ( sServiceName == "rbo3020-service:searchStndYear" ) {
		if(dsStrYear.GetRowCount() >0){
			setStndYear(dsStrYear.GetColumn(0,"STND_YEAR"));
		}

		fcd_GetTmsDs("dsTms", "001", cboStndYear.Value);

		fcRollEndSearch();

		fcSearch();
	}

	// 용지마감조회 
	if ( sServiceName == "rbo3020-service:searchRollEnd" ) {

	}


}




