function fcRollEndSearch(){

	var sServiceName = "rbo3020-service:searchRollEnd";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollEnd=dsRollEnd ";

	fcd_AddParam("STND_YEAR",cboStndYear.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}



function fcdsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	    switch ( obj.id ) {
		case "dsRollBrncMove" :
			if(strColumnID == "ROLL_GBN"){


				if(dsRollEnd.FindRow("ROLL_GBN",varNewValue) >-1 ){
					if(cboRollGbn.Value == null || cboRollGbn.Value == ""){
						fnc_Message(SNIS_CRA_J002);

					}

					dsRollBrncMove.SetColumn(nRow,"ROLL_GBN", "");
				}
			}			
			break;


	}

}
