/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	//조회 완료 후
	if ( sServiceName == "rbo4010-service:searchList" ) {

	}			

	//저장 완료 후
	if ( sServiceName == "rbo4010-service:SavePartsModel" ) {
		fcSearch();
	}	

	//이미지 파일(첨부파일) 조회 완료 후
	if ( sServiceName == "common_file-service:searchFileMana" ) {
		imgModel.ImageID = "";

		if( dsUploadFile.rowcount > 0 ) {
			sFilePath = GBL_SERVER_UPLOAD_DIR + SubStr(GBL_MENUID,0,3) + "/" 
			          + dsUploadFile.GetColumn(0, "REAL_FILE_NM");
			imgModel.ImageID = sFilePath;
		}
	}

}


