}


/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcAddRow(obj) {
	// dsList ADD ROW
	if (obj.GetForm().Id == "revBtnRow") {

		var nRow;	// Record Add
		nRow = dsList.AddRow();

		cboUseYn.Index = 0;		// 사용여부	( default : 사용 )
		cboMadeGbn.Index = 0;	// 원산지구분 ( default : 국산 )


		if(Length(cboSchPartsLcd.Value) >0){
			dsList.SetColumn(nRow,"PARTS_LCD",cboSchPartsLcd.Value);
		}
		edPartsNm.SetFocus();

	// FILE UPLOAD ADD ROW
	} else if (obj.GetForm().Id == "fileBtnRow") {

		var sResponse = fcd_GetSelectFile("Open", "", "IMG");  

		if ( fnc_IsNull(sResponse) ) return;

		if( dsUploadFile.rowcount > 0 ) {
			dsUploadFile.DeleteRow(0);
		}

		var nRow = dsUploadFile.addRow();

		dsUploadFile.SetColumn(nRow, "ATT_FILE_KEY", dsList.GetColumn(dsList.row,"ATT_FILE_KEY"));
		dsUploadFile.SetColumn(nRow, "FILE_PATH"   , sResponse[0] + "\\" + trim(sResponse[1]));
		dsUploadFile.SetColumn(nRow, "FILE_NM"     , trim(sResponse[1]));

		imgModel.ImageID =  sResponse[0] + "\\" + trim(sResponse[1]); 
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcDelRow(obj) {
	if (obj.GetForm().Id == "revBtnRow") {	// dsList 삭제 버튼

		//선택된 Row Count
		var iSelectRowCount = fnc_GetColumnValueCount(dsList, "CHK", "1");

		if ( iSelectRowCount <= 0 ) {
			// 삭제할 자료가 없습니다.
			fnc_Message(SNIS_COM_1003, "N");
			return false;
		}

		var nRow = dsList.row;
		var strSeq = dsList.GetColumn(nRow, "STK_CNT");

		if ( !fnc_IsNull(strSeq) ) {
			// 상세정보가 있어서 삭제할 수 없습니다.
			fnc_Message(SNIS_RBM_B001, "N", nRow + 1, "상세정보");
			return;

		} 

		// 선택된 자료 [ @ ]건를 삭제 하시겠습니까?
		var bResult = fnc_Message(SNIS_COM_1010, "Y", iSelectRowCount);
		if ( bResult ) {
			fnc_DeleteRow(dsList, "CHK", "1");
		}

			return bResult;

	} else if (obj.GetForm().Id == "fileBtnRow") {	// 업로드 삭제버튼

		if( dsUploadFile.rowcount > 0 ) {
			dsUploadFile.SetColumn(0, "CHK", 1);
		}

		if ( !fnc_DeleteCheck(dsUploadFile, "CHK", "1") ) return;
		imgModel.ImageID = "";
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  그리드 더블 클릭시
+-------------------------------------------------------------------------------------------------*/

function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(nRow, "FILE_NM"));
	var sServerFileName = dsUploadFile.GetColumn(nRow, "FILE_PATH");
	var sFileSize       = dsUploadFile.GetColumn(nRow, "FILE_SIZE");
	if ( sLocalFileName == null ) return;

	fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFile);
}


/*-------------------------------------------------------------------------------------------------+
>>  그 밖의 처리
+-------------------------------------------------------------------------------------------------*/

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch( obj.id ) {
		case "dsList" :
			if( strColumnID == "PARTS_LCD" ) {		
				if( varNewValue == "001" ) dsMcd.Filter( "CD_TERM1==" + quote(varNewValue) );
				if( varNewValue == "002" ) dsMcd.Filter( "CD_TERM2==" + quote(varNewValue) );
				if( varNewValue == "003" ) dsMcd.Filter( "CD_TERM3==" + quote(varNewValue) );

				cboPartsMcd.Enable = true;
				cboPartsMcd.Index = 0;
			}
		break;
	}
}


function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch( obj.id ) {
		case "dsList" :
			// 신규일 시에만 수정 가능
			if(obj.getRowType(nRow) == "insert") {
				cboPartsLcd.Enable = true;
				cboPartsMcd.Enable = true;
				edPartsNm .Enable  = true;
				cboMadeGbn.Enable  = true;
				cboMadeGbn.Index = 0;
			} else {
				cboPartsLcd.Enable = false;
				//cboPartsMcd.Enable = false;
				edPartsNm .Enable  = false;
				//cboMadeGbn.Enable  = false;


				if( cboPartsLcd.Value == "001" ) dsMcd.Filter( "CD_TERM1==" + quote(cboPartsLcd.Value) );
				if( cboPartsLcd.Value == "002" ) dsMcd.Filter( "CD_TERM2==" + quote(cboPartsLcd.Value) );
				if( cboPartsLcd.Value == "003" ) dsMcd.Filter( "CD_TERM3==" + quote(cboPartsLcd.Value) );


				fcSearchFile( obj.GetColumn(nRow, "ATT_FILE_KEY") );
			}

			// Filter 조절
			if( fnc_IsNull(obj.GetColumn(nRow, "PARTS_LCD")) ) {
				dsMcd.Filter("CD==''");
			} 
		break;
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  조회조건에서 Enter key event 발생時 처리
+-------------------------------------------------------------------------------------------------*/

function fcEnterKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13) fcSearch();
}



function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch( obj.Id ) {
		case "cboSchPartsLcd" :
				var strFilter="";
				if( strCode == "001") strFilter = "CD_TERM1=='001'";
				if( strCode == "002") strFilter = "CD_TERM2=='002'";
				if( strCode == "003") strFilter = "CD_TERM3=='003'";		
				dsPartsMcd.Filter(strFilter);
				cboSchPartsMcd.Enable 	= true;
				cboSchPartsMcd.Index 	= 0;

				fcSearch();
		break;
	}
}


// 발매기 '전체' 삽입
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.Id ) {
		case "dsPartsLcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )
				dsLcd.Copy(obj);

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.fireEvent = true;
				cboSchPartsLcd.Index = 0;
			}
		break;

		case "dsPartsMcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )
				dsMcd.Copy(obj);

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.setColumn(0,"CD_TERM1","001");
				obj.setColumn(0,"CD_TERM2","002");
				obj.setColumn(0,"CD_TERM3","003");
				obj.fireEvent = true;
				cboSchPartsMcd.Index = 0;
			}
		break;
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
		switch ( obj.Id ) {
			case "cboSchPartsMcd" :
				fcSearch();

			break;
		}
}

/*-------------------------------------------------------------------------------------------------+
>>  파일 다운받기
+-------------------------------------------------------------------------------------------------*/
function fcImgOnClick(obj,nX,nY)
{
	switch ( obj.id ) 
	{	
		case "imgModel" : 
			if (imgModel.ImageID == "") return;

			var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(0, "FILE_NM")); // 파일명			
			var sServerFileName = dsUploadFile.GetColumn(0, "FILE_PATH");	// 서버의 파일위치 및 파일명
			var sFileSize       = 0;
			if ( sLocalFileName == null ) return;

			fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName);
			break;
	}
}
