function dsMenuGrpList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

    //메뉴ID 중복체크
    if(strColumnID == "MN_ID") {
        //메뉴리스트, 메뉴그룹에서 체크
        if(fnc_IsColumnDuplicate(obj, strColumnID)) {   
            obj.SetColumn(nRow,strColumnID,VarOldValue);
            //grdMnList.SetFocus();

            fnc_Message(L_COM_ALT_0016, "SERVERMSG", "메뉴ID가");
            return;

        }
        if ( fnc_GetExistValue(dsMenuGrpValue, strColumnID, varNewValue) != -1 ) {
            obj.SetColumn(nRow, strColumnID, varOldValue);
            //grdMnList.SetFocus();

			fnc_Message(L_COM_ALT_0016, "SERVERMSG", "기존 메뉴ID가");
	    }


    } 

    //메뉴그룹 중복체크(0레벨일 경우만 체크)
    if(strColumnID == "ORD_NO" && dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "LVL") == 0 && dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_LEVEL") == '000000') {
        if(fnc_IsColumnDuplicate(obj, strColumnID)) {
            obj.SetColumn(nRow,strColumnID,VarOldValue);
            //grdMnList.SetFocus();

            fnc_Message(L_COM_ALT_0016, "SERVERMSG", "메뉴그룹이");

        }
    } 

    //정렬순서 중복체크
    if(strColumnID == "MN_LEVEL") {
        if(fnc_IsColumnDuplicate(obj, strColumnID)) {
            obj.SetColumn(nRow,strColumnID,VarOldValue);
            //grdMnList.SetFocus();

            fnc_Message(L_COM_ALT_0016, "SERVERMSG", "정렬순서가");

        }
    } 

}

function Combo0_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
	if(combo0.Value == "0"){
		grid.UnFilter();
	}else{
		grid.Filter("col1="+quote(combo0.Text));
	}
}

function Combo1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{ 
	if(obj.Value == "0"){
		grid.UnFilter();
	}else{
		grid.Filter("col3="+quote(obj.Text));
	}

}


