/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj)
{
	dsMycatNoMapping.ClearData();
	dsWinNoMapping.ClearData();
	dsGreenZone.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbr1030-service:searchWinNoMapping";
    var sInDataSet   = "";
    var sOutDataSet  = "dsWinNoMapping=dsWinNoMapping dsMycatNoMapping=dsMycatNoMapping";     

	fcd_AddParam("COMM_NO"  , cboBrncCd.value);	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

