/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	var sGrnZnNo, sGrnZnNm, sStrDy, sEndDy;
	for(var i=0; i<dsGreenZone.rowcount; i++) {
	    sCommNo  = dsGreenZone.GetColumn(i, "COMM_NO");
	    sGrnZnNo = dsGreenZone.GetColumn(i, "GRN_ZN_NO");
	    sGrnZnNm = dsGreenZone.GetColumn(i, "GRN_ZN_NM");
	    sStrDy   = dsGreenZone.GetColumn(i, "STR_DY");
	    sEndDy   = dsGreenZone.GetColumn(i, "END_DY");
	    for(var j=i+1; j<dsGreenZone.rowcount;j++) {
			if( sCommNo+sGrnZnNo == dsGreenZone.GetColumn(j, "COMM_NO")+dsGreenZone.GetColumn(j, "GRN_ZN_NO") ) {
				if (sStrDy <= dsGreenZone.GetColumn(j, "STR_DY") &&
				    sEndDy <= dsGreenZone.GetColumn(j, "STR_DY") ) {
					fnc_message(sGrnZnNm + "그린존(투표소)의 시작일자가 중복됩니다.","N");
					dsGreenZone.row = j;
					return false;
				}
				if (sStrDy <= dsGreenZone.GetColumn(j, "END_DY") &&
				    sEndDy <= dsGreenZone.GetColumn(j, "END_DY") ) {
					fnc_message(sGrnZnNm + "그린존(투표소)의 종료일자가 중복됩니다.","N");
					dsGreenZone.row = j;
					return false;
				}
			}
		}  
	}

	for(var i=0; i<dsWinNoMapping.rowcount; i++) {
	    sWIN_NO  = dsWinNoMapping.GetColumn(i, "WIN_NO");
	    for(var j=i+1; j<dsWinNoMapping.rowcount;j++) {
			if( sWIN_NO == dsWinNoMapping.GetColumn(j, "WIN_NO") ) {
				fnc_message(sGrnZnNm + "발매창구 번호가 중복됩니다.","N");
				dsWinNoMapping.row = j;
				return false;
			}
		}  
	}

	for(var i=0; i<dsMycatNoMapping.rowcount; i++) {
	    sDEV_NM  = dsMycatNoMapping.GetColumn(i, "DEV_NM");
	    for(var j=i+1; j<dsMycatNoMapping.rowcount;j++) {
			if( sDEV_NM == dsMycatNoMapping.GetColumn(j, "WIN_NO") ) {
				fnc_message(sDEV_NM + ":그린카드단말 번호가 중복됩니다.","N");
				dsMycatNoMapping.row = j;
				return false;
			}
		}  
	}

	return true;
}

function fcSave(obj)
{
	if (fcSaveCheck(obj) == false) return;
	if(fnc_Message(SNIS_COM_1012, "Y") == false) return;

	var sServiceName = "rbr1030-service:saveGreenZone";
	var sInDataSet   = "dsGreenZone=dsGreenZone:u "
					 + "dsWinNoMapping=dsWinNoMapping "
					 + "dsMycatNoMapping=dsMycatNoMapping ";
	var sOutDataSet  = "";		

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

