/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후 
	if ( sServiceName == "rbr1030-service:searchGreenZone" ) {
		dsWinNo.copy(dsWinNoMapping);
		fcSetFilter(0);		
	} else if ( sServiceName == "rbr1030-service:saveGreenZone" ) {
		fcSearch();
	} else if (sServiceName == "rbr1030-service:searchWinNoDupCnt")  {
		if (dsWinNoDupCnt.rowcount() > 0) {
			fnc_message("중복된 자료가 존재합니다.","N");
		}
	} else if ( sServiceName = "common-service:searchCode" ) {			// 공통코드조회후 해당 지점의 상품분류 조회
		if (sApro != "T") {			
			dsBrncCd.Filter("CD_TERM2='"+GdsUser.GetColumn(0, "TEAM_CD")+"'");	
		}
		cboBrncCd.index = 0;	
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if (obj.GetForm().id == "divAddRowGrn") {		
		var max_GrnZnNo = 0;
		var sGrnZoNo = "";
		for (var i=0;i<dsGreenZone.RowCount();i++) {
			if (max_GrnZnNo < toNumber(dsGreenZone.GetColumn(i, "GRN_ZN_NO"))) {
				max_GrnZoNo = toNumber(dsGreenZone.GetColumn(i, "GRN_ZN_NO"));
			}
		}
		sGrnZoNo = Right("000" + toString(max_GrnZoNo + 1), 3);

		var nRow  = dsGreenZone.AddRow();
		dsGreenZone.SetColumn(nRow, "COMM_NO"  , gdsUser.GetColumn(0, "BRNC_CD"));
		dsGreenZone.SetColumn(nRow, "GRN_ZN_NO", sGrnZoNo);
		dsGreenZone.SetColumn(nRow, "STR_DY",    today());
		dsGreenZone.SetColumn(nRow, "END_DY",    "29991231");
		dsGreenZone.SetFocus(); // 입력 첫 항목으로 Focus	
	} else if (obj.GetForm().id == "divAddRowGrn") {
		var nRow  = dsGreenZone.AddRow();
		dsWinNoMapping.SetColumn(nRow, "GRN_ZN_NO", dsGreenZone.GetColumn(dsGreenZone.row, "GRN_ZN_NO"));
		dsWinNoMapping.SetColumn(nRow, "COMM_NO",   dsGreenZone.GetColumn(dsGreenZone.row, "COMM_NO"));
		dsWinNoMapping.SetColumn(nRow, "STR_DY",    today());
		dsWinNoMapping.SetColumn(nRow, "END_DY",    "29991231");
		//dsWinNoMapping.SetFocus(); // 입력 첫 항목으로 Focus	
	} else if (obj.GetForm().id == "divAddRowWin") {	//발매창구번호 추가
		var nRow  = dsWinNoMapping.AddRow();
		dsWinNoMapping.SetColumn(nRow, "COMM_NO",   gdsUser.GetColumn(0, "BRNC_CD"));
		//dsWinNoMapping.SetFocus(); // 입력 첫 항목으로 Focus	
	} else if (obj.GetForm().id == "divAddRowMycat") {	//그린카드 단말번호 추가
		var nRow  = dsMycatNoMapping.AddRow();
		dsMycatNoMapping.SetColumn(nRow, "CHNL_CD", "001");
		dsMycatNoMapping.SetColumn(nRow, "COMM_NO", gdsUser.GetColumn(0, "BRNC_CD"));
		//dsMycatNoMapping.SetFocus(); // 입력 첫 항목으로 Focus	
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if (obj.GetForm().id == "divAddRowGrn") {	//발매창구번호 추가
		if ( !fnc_DeleteCheck(dsGreenZone, "chk", "1") ) return;
	} 
	if (obj.GetForm().id == "divAddRowWin") {	//발매창구번호 추가
		if ( !fnc_DeleteCheck(dsWinNoMapping, "chk", "1") ) return;
	} 
	if (obj.GetForm().id == "divAddRowMycat") {	//발매창구번호 추가
		if ( !fnc_DeleteCheck(dsMycatNoMapping, "chk", "1") ) return;
	} 
}
