function fcgrdOnDrag(obj,nPosX,nPosY,varKeyState,nRow,nCell,nPivotIndex)
{
    var dataObj = CreateDataObject();
	dataObj.SetData("MOT_TEXT", obj.ID);
	dataObj.SetData("BOAT_TEXT", obj.ID);

	if ( obj.IsAboveSel() ) return dataObj;	
}

function fcgrdOnDrop(obj,objDragObj,objDataObj,nPosX,nPosY,varKeyState,nRow,nCell,nPivotIndex)
{	
    var objName       = "grdMot";

	var sTargetMotNo = substr(obj.Id, length(objName));
    var objTargetDs   = dsTellerArrange;

	if ( objDataObj.GetData("MOT_TEXT") == "grdMot" ) // MultiLine Grid 에서 온경우
	{
        if ( nRow < 0 ) return;
        fcDiv2TellerAlloc(objTargetDs, nRow, dsTellerArrange, dsTellerArrange.currow);
        objTargetDs.row = nRow;

        oLastDs       = objTargetDs;
        oLastDsRow    = nRow;

        return;
	}

	var sSourceMotNo = substr(objDataObj.GetData("MOT_TEXT"), length(objName));
    var objSourceDs   = Object("dsTellerArrange" + sSourceMotNo);

    if ( nRow < 0 ) {
        oLastDs       = objTargetDs;
        oLastDsRow    = objSourceDs.currow;

        dsOutRacerArrange.AddRow();
        fcDiv2TellerAlloc(dsTellerArrange, dsTellerArrange.currow, oLastDs, oLastDsRow);
    } else {
        dsOutRacerArrangeTemp.AddRow();
        fcDiv2TellerAlloc(dsOutRacerArrangeTemp, dsOutRacerArrangeTemp.currow, objTargetDs, nRow);
        fcDiv2TellerAlloc(objTargetDs, nRow, objSourceDs, objSourceDs.currow);
        fcDiv2TellerAlloc(objSourceDs, objSourceDs.currow, dsOutRacerArrangeTemp, dsOutRacerArrangeTemp.currow);

        oLastDs         = objTargetDs;
        oLastDsRow      = nRow;
        objTargetDs.row = nRow;
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  투표소/텔러 세팅
+-------------------------------------------------------------------------------------------------*/
function fcDiv2TellerAlloc(objTarget, nRowTarget, objSource, nRowSource, nCellSource)
{
	if (objTarget.rowcount() <= nRowTarget) return;

    var sColumnText = "";
    sColumnText = tab0.tab3.grdTellerArrange.GetCellText("Head", nRowSource, nCellSource);

    for ( var i = 0; i < objSource.GetColCount(); i++ ) {
        var sColumnID = objSource.GetColID(i);
        var srcColumn = "";

        //팝업창에서 장비세팅할때
        srcColumn   = "BOAT_NO"; 
        if ( srcColumn != null && sColumnID != null && sColumnID == srcColumn ) {
            //값을 세팅
            objTarget.SetColumn(nRowTarget, srcColumn, objSource.GetColumn(nRowSource, sColumnID));
            objSource.SetColumn(nRowSource, sColumnID, "");
        }
    }

    if ( objSource.ID == "dsOutMotLot"  || objSource.ID == "dsOutMotLotTemp") objSource.DeleteRow(nRowSource);
}
