>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 장비  조회 완료 후
	if ( sServiceName == "rbr2010-service:searchEquipMana" ) {

	}


	// 장비  저장 완료 후
	if ( sServiceName == "rbr2010-service:saveEquipMana" ) {
		fcSearch();
	}
}




/*-------------------------------------------------------------------------------------------------+
>> Enter Key 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/


function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(obj.Id == "cboTypeCd"){
		fcSetCellByEquipGbn();
		fcSearch();
	}else if(obj.Id == "cboBrncCd"){
		fcd_SetEnableByBrnc(grdEquipMana,cboBrncCd.value);
		fcd_SetEnableByBrnc(equipBtnRow,cboBrncCd.value);
		fcSearch();
	}

}


