/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsLeaseMana.ClearData();

	dsLeaseBond.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbr3010-service:searchLeaseMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsLeaseMana=dsLeaseMana ";
		//sOutDataSet += "dsIpMana=dsIpMana ";

	fcd_AddParam("BRNC_CD",cboBrncCd.Value);
	fcd_AddParam("CNTR_STR_DT", calCntrStrDt.Value);
	fcd_AddParam("CNTR_END_DT", calCntrEndDt.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>> 채권현황  SW 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchLeaseBond(){
		 sServiceName = "rbr3010-service:searchLeaseBond";
		 sInDataSet   = "";
		 sOutDataSet  = "dsLeaseBond=dsLeaseBond ";

		 fcd_AddParam("BRNC_CD", dsLeaseMana.GetColumn(dsLeaseMana.row, "BRNC_CD"));
		 fcd_AddParam("CNTR_STR_DT", dsLeaseMana.GetColumn(dsLeaseMana.row, "CNTR_STR_DT"));


		 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsLeaseMana) && !fnc_DatasetChangeCheck(dsLeaseBond) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
		}


	// 필수항목체크
	oDataSet          = dsLeaseMana;
	sMandatoryColID   = "BRNC_CD|CNTR_STR_DT";
	sMandatoryColName = "지점코드|계약기간(시작일)";
	oGrid             = grdLeaseMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	// 필수항목체크
	oDataSet          = dsLeaseBond;
	sMandatoryColID   = "BANK_NM|SECU_AMT|ACPT_DT";
	sMandatoryColName = "은행명|담보금액|접수일자";
	oGrid             = tbLease.tbLeaseBond.grdLeaseMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	for ( i = 0; i <dsLeaseMana.GetRowCount(); i++ ) {
		if ( dsLeaseMana.GetRowType(i) != "normal" ) {
			if(!fnc_CheckDateBetween(dsLeaseMana.GetColumn(i,"CNTR_STR_DT"), 
					dsLeaseMana.GetColumn(i,"CNTR_END_DT"))){


					return false;
			}
		}

	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbr3010-service:saveLeaseMana";
	sInDataSet   = "dsLeaseMana=dsLeaseMana:u dsLeaseBond=dsLeaseBond:u";
	//sInDataSet  += "dsSpecCd=dsSpecCd:u ";

	sOutDataSet  = "dsLeaseMana=dsLeaseMana ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( fnc_DatasetChangeCheck(dsLeaseMana) ) {
				//fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//return;
			}

			dsLeaseMana.AddRow(); // Record Add

			tbLease.tbLeaseMana.cboBrncCd2.SetFocus();

			break;
		case "dtlBtnRow" :

			var nRow = dsLeaseBond.AddRow(); // Record Add
			dsLeaseBond.SetColumn(nRow,"BRNC_CD", dsLeaseMana.GetColumn(dsLeaseMana.currow,"BRNC_CD") );
			dsLeaseBond.SetColumn(nRow,"CNTR_STR_DT", dsLeaseMana.GetColumn(dsLeaseMana.currow,"CNTR_STR_DT") );

			break;

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {
	dsLeaseMana.InsertRow(dsLeaseMana.GetCurrow());           // Record Ins
}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :

	        if ( fnc_GetColumnValueCount(dsLeaseMana, "CHK", "1") <= 0 ) {
				fnc_Message(SNIS_COM_1003, "N");
				return false;
			}

			if ( !fnc_Message(SNIS_RBM_A009,"Y")) return;

			fnc_DeleteRow(dsLeaseMana, "chk", "1");

			//dsGrpCd_OnRowPosChanged();
			break;

		case "dtlBtnRow" :	//장비 SW 삭제 
			if ( !fnc_DeleteCheck(dsLeaseBond, "chk", "1") ) return;

			break;

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "grdLeaseMana:임대관리";
	fcd_OpenExcelListExport(sExcelList,1);



	var strGrid = "grdLeaseMana|1"
					+",tbLease.tbLeaseBond.grdLeaseBond|1";
	var strSheet = "채권현황,임대관리";
	var strFileNm = "임대관리";

	fcd_topGfnExport(strSheet,strGrid,strFileNm,this) ;

}


