*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// IP 조회 완료 후
	if ( sServiceName == "rbr3010-service:searchLeaseMana" ) {

		tbLease.TabIndex = 0;
	}


	// IP 저장 완료 후
	if ( sServiceName == "rbr3010-service:saveLeaseMana" ) {
		fcSearch();

	}
}



/*-------------------------------------------------------------------------------------------------+
>> dataset OnOnColumnChanged  이벤트 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	switch ( obj.id ) {
		case "dsLeaseMana" :
			if(strColumnID == "COMM_AREA_SQMT"){

					dsLeaseMana.setColumn(nRow,"COMM_AREA_PY", fnc_Sqmt(varNewValue, 0)    );
					//dsLeaseMana.setColumn(nRow,"COMM_AREA_PY", ROUND(dsLeaseMana.GetColumn(nRow,"COMM_AREA_SQMT") /3.305785)    );

			}

			if(strColumnID == "ONLY_AREA_SQMT"){

					dsLeaseMana.setColumn(nRow,"ONLY_AREA_PY", fnc_Sqmt(varNewValue, 0)    );
					//dsLeaseMana.setColumn(nRow,"ONLY_AREA_PY", ROUND(dsLeaseMana.GetColumn(nRow,"ONLY_AREA_SQMT") /3.305785)    );

			}
			break;

	}
}

// 우편번호 찾기
function fcPostDialoge(obj)
{
	var sFormId 	= "";
	var sArgs 		= "";
	var arrReturn   = array(2);
	var nRow		= dsLeaseMana.row;

	sFormId = "common::comPost.xml";
	sArgs = "";	

	if( dsCommDesc.rowcount == 0 )
	{
		fnc_Message(SNIS_RBM_A002, "N");
		return;
	}

	arrReturn = Dialog(sFormId, sArgs, 334, 443, "");

	dsLeaseMana.SetColumn(nRow, "POST_CD", replace(arrReturn[0],"-",""));
	dsLeaseMana.SetColumn(nRow, "ADDR"   , arrReturn[2]);
	tbLease.tbLeaseMana.edDetlAddr.SetFocus();
}

/*-------------------------------------------------------------------------------------------------+
>> dataset OnLoad 이벤트 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsSearchBrncCd" :
			dsSearchBrncCd.InsertRow(0);
			dsSearchBrncCd.SetColumn(0, "CD_NM", "전체");
			cboBrncCd.index = 0;
			break;
		case "dsBrncCd" :
			//dsBrncCd.InsertRow(0);
			//dsBrncCd.SetColumn(0, "CD", "");
			//dsBrncCd.SetColumn(0, "CD_NM", "");
			break;
		case "dsBondSecuObj" :
			dsBondSecuObj.InsertRow(0);
			dsBondSecuObj.SetColumn(0, "CD_NM", "선택");
			tbLease.tbLeaseMana.cboBondSecuObj.index = 0;
			break;
		case "dsBondSecuRank" :
			dsBondSecuRank.InsertRow(0);
			dsBondSecuRank.SetColumn(0, "CD_NM", "선택");
			tbLease.tbLeaseMana.cboBondSecuRank.index = 0;
			break;

	}
}

/*-------------------------------------------------------------------------------------------------+
>> grid  OnExpandUp 이벤트 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
 	switch ( obj.id ) {
		case "grdLeaseMana" :
			var sFormUrl 	= "";
			var sArgs 		= "";
			var arrReturn   = array(2);

			if (nCell == 2) {
				sFormUrl = "common::comPost.xml";
				sArgs = "";	

				arrReturn = Dialog(sFormUrl, sArgs, 334, 443, "");

				dsLeaseMana.SetColumn(dsLeaseMana.currow,"POST_CD",arrReturn[0]);
				dsLeaseMana.SetColumn(dsLeaseMana.currow,"ADDR",arrReturn[2]);


			}else if (nCell == 9 || nCell == 10 || nCell ==18 || nCell ==19) {

				fnc_SetPopupDivCalendar(obj, nRow, nCell);

			}
			break;

		case "grdLeaseBond" :

			if (nCell == 3) {
				fnc_SetPopupDivCalendar(obj, nRow, nCell);

			}

			break;

	}

}


function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
			case "cboBrncCd" :
				fcSearch();
				break;

	 }
}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{ 
	//기본키(지점코드)는 수정 불가능
	if(dsLeaseMana.getRowType(nRow) == "insert") {
		tbLease.tbLeaseMana.cboBrncCd2.Enable = true;
		tbLease.tbLeaseMana.calCntrStrDt2.Enable = true;
		tbLease.tbLeaseMana.cboBrncCd2.SetFocus();

		dsLeaseBond.ClearData();

	} else {
		tbLease.tbLeaseMana.cboBrncCd2.Enable = false;
		tbLease.tbLeaseMana.calCntrStrDt2.Enable = false; 
		tbLease.tbLeaseMana.calCntrEndDt2.SetFocus();

		fcSearchLeaseBond();
	}
}

function fcEdOnChanged(obj,strText)
{
		switch ( obj.id ) {
			case "mskOnlyAreaSqmt" :
				tbLease.tbLeaseMana.mskTotAreaSqmt.value = ToFloat(tbLease.tbLeaseMana.mskOnlyAreaSqmt.Value)  + ToFloat(tbLease.tbLeaseMana.mskCommAreaSqmt.Value) ;	
				tbLease.tbLeaseMana.mskTotAreaPy.value = ToFloat(tbLease.tbLeaseMana.mskOnlyAreaPy.Value)  + ToFloat(tbLease.tbLeaseMana.mskCommAreaPy.Value) ;	

				break;
			case "mskCommAreaSqmt" :
				tbLease.tbLeaseMana.mskTotAreaSqmt.value = ToFloat(tbLease.tbLeaseMana.mskOnlyAreaSqmt.Value)  + ToFloat(tbLease.tbLeaseMana.mskCommAreaSqmt.Value) ;
				tbLease.tbLeaseMana.mskTotAreaPy.value = ToFloat(tbLease.tbLeaseMana.mskOnlyAreaPy.Value)  + ToFloat(tbLease.tbLeaseMana.mskCommAreaPy.Value) ;	
				break;

		}
}


function fcTabOnChanging(obj,nOldIndex,nNewIndex)
{
	switch ( nNewindex ) {

		case 0:	// 기본정보

			break;

		case 1 : //채권현황 

			if(dsLeaseMana.GetRowCount() <=0 || dsLeaseMana.GetRowType(dsLeaseMana.row) == "insert"){
				fnc_Message(SNIS_RBM_A001, "","저장된 임대","채권");

				tbLease.TabIndex = nOldIndex;
			}

			break;


	}


}
