	// }

	dsList.AddRow(); // Record Add
	dsList.SetFocus(); // GRID¿¡ ADD ÇÑ ROW ÀÇ Ã¹¹øÂ° Ç×¸ñ¿¡ ÀÔ·ÂFocus¸¦ ÁØ´Ù.



	/*  ÇÊ¼öÀÔ·Â°ª ¼ÂÆÃ
	 * 1. ½Ã°³±ÝÄÚµå   - ÀúÀåÇÒ ¶§ SQL·Î ±¸ÇÑ´Ù.
	 */	 



	/*  
	 * ÃÊ±â°ª ¼ÂÆÃ : 
	 */    
