function fcInit(obj) {
	if (tab0.TabIndex == 0) dsHnrRefreReqWeb.ClearData();
	else dsHnrRefreReq.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1120-service:searchWebNotice";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsWebNotice=dsWebNotice";     

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);        	// 경륜/경정 구분
	fcd_AddParam("STND_YEAR", cbStndYear.Value);       // 기준년도

	if (tab0.TabIndex == 1) {
		sServiceName = "rbs1120-service:searchHnrRefreRecv";  // Grid 조회
		sInDataSet   = "";
		sOutDataSet  = "dsHnrRefreReq=dsHnrRefreReq";     
	}
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchHnrRefreReq(obj) {
	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs1120-service:searchWebRecv";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreReqWeb=dsHnrRefreReqWeb";     	

	fcd_AddParam("WEB_PARENT_SEQ_ID", dsWebNotice.GetColumn(dsWebNotice.row, "WEB_PARENT_SEQ_ID"));        // 경륜/경정 구분	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchStndYear(obj) {

	var sServiceName = "rbs1110-service:searchYearList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsYear=dsYear";     

	fcd_AddParam("MEET_CD"      , rdoMeetCd.Value);        // 경륜/경정 구분

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

/*-------------------------------------------------------------------------------------------------+
>>  가져오기 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcConvWebHnrRefreReq(obj) {

	var sServiceName = "rbs1120-service:convHnrRefreWebRecv";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);          // 경륜/경정 구분
	fcd_AddParam("STND_YEAR", cbStndYear.Value);        // 기준년도

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs1120-service:saveHnrRefreReq";
	var sInDataSet   = "dsHnrRefreReq=dsHnrRefreReq:u";
	var sOutDataSet  = "dsHnrRefreReq=dsHnrRefreReq";

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);          // 경륜/경정 구분
	fcd_AddParam("STND_YEAR", cbStndYear.Value);        // 기준년도

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

