/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		if (dsHnrRefreReq.ColCount() == 0) {
			fnc_message("먼저 조회후 작업하세요!", "N");
			return;
		}
		nRow = dsHnrRefreReq.AddRow(); 								// Record Add
		dsHnrRefreReq.SetColumn(nRow, "MEET_CD",   rdoMeetCd.Value);
		dsHnrRefreReq.SetColumn(nRow, "STND_YEAR", cbStndYear.Value);
		dsHnrRefreReq.SetColumn(nRow, "INST_DT", Today());		
	}		
}


