}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {

		var dCnt = 0;
		for(var i = 0; i < dsHnrRefreReq.GetRowCount(); i++) {
			if(dsHnrRefreReq.GetColumn(i, "CHK") == 1) dCnt ++;
			if(dsHnrRefreReq.GetColumn(i, "WNR_CNT") > 0) {
				fnc_message("확인","이미 명예심판이 선정된 차수는 삭제가 불가능합니다\n선정자를 삭제후 차수 삭제가 가능합니다.", "N");
				return;
			}
		}
		if(fnc_Message(SNIS_COM_1010, "Y", dCnt) == 0) return false;	

		for(var i = dsHnrRefreReq.GetRowCount() -1; i >= 0 ; i--) {
			if(dsHnrRefreReq.GetColumn(i, "CHK") == 1) dsHnrRefreReq.deleterow(i);
		}
	}
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {
		// 공통코드 조회후 처리(년도를 조회해야 하므로)
		//fcSearch();
	} else if ( sServiceName == "rbs1110-service:searchYearList" ) { //  기준년도 조회후
		fcSearch();  // 조회한다.
	} else if ( sServiceName == "rbs1120-service:searchWebNotice" ) { //  기준년도 조회후
		fcSearchHnrRefreReq();  // 해당 공지사항에 대한 신청내역을 조회한다.
	} else if ( sServiceName == "rbs1120-service:searchWebRecv" ) { // 완료 후
		//Trace(dsHnrRefreReq.savexml());
	} else if ( sServiceName == "rbs1120-service:saveHnrRefreReq" ) { // 저장 완료 후
		fcSearch();  // 다시 조회한다.
	}			


}


