/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);  // 그리드 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsHnrRefreReq)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsHnrRefreReq;
	sMandatoryColID   = "MEET_CD|STND_YEAR|REQ_USER_NM|HP_NO";
	sMandatoryColName = "경륜경정구분|행사년도|고객명"|"휴대폰번호";
	oGrid             = grdHnrRefreTms;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}


