function fcSearch(obj) {
	if (!fcSearchCheck(obj)) return;// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­
	dsHnrRefreWnr.clear();

	var sServiceName = "rbs1110-service:searchHnrRefreTms";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreTms=dsHnrRefreTms";  	

	fcd_AddParam("MEET_CD"  , rdoMeetCd.Value);        	// °æ·û/°æÁ¤ ±¸ºÐ
	fcd_AddParam("STND_YEAR", cbStndYear.Value);       	// ±âÁØ³âµµ

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchHnrRefreWnr(obj) {
	if (!fcSearchCheck(obj)) return;// ÇÊ¼öÁ¶°Ç Ã¼Å© ¹× ±×¸®µå ÃÊ±âÈ­

	var sServiceName = "rbs1130-service:searchHnrRefreWnr";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsHnrRefreWnr=dsHnrRefreWnr";     	

	fcd_AddParam("MEET_CD"  , dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "MEET_CD"));        	// °æ·û/°æÁ¤ ±¸ºÐ
	fcd_AddParam("STND_YEAR", dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "STND_YEAR"));       	// ±âÁØ³âµµ
	fcd_AddParam("TMS",       dsHnrRefreTms.GetColumn(dsHnrRefreTms.row, "TMS"));       		// Â÷¼ö

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
function fcSearchStndYear(obj) {

	var sServiceName = "rbs1110-service:searchYearList";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsYear=dsYear";     

	fcd_AddParam("MEET_CD"      , rdoMeetCd.Value);        // °æ·û/°æÁ¤ ±¸ºÐ

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

