*                          CALL BACK Ã³¸® ºÎºÐ                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// °øÅëÄÚµå Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "common-service:searchCode" ) {
		// °øÅëÄÚµå Á¶È¸ÈÄ Ã³¸®(³âµµ¸¦ Á¶È¸ÇØ¾ß ÇÏ¹Ç·Î)
		//fcSearch();
	} else if ( sServiceName == "rbs1110-service:searchYearList" ) { //  ±âÁØ³âµµ Á¶È¸ÈÄ
		fcSearch();  // Á¶È¸ÇÑ´Ù.
	} else if ( sServiceName == "rbs1110-service:searchHnrRefreTms" ) { //  Â÷¼ö Á¶È¸
		if (dsHnrRefreTms.RowCount() == 0) {
			fnc_message("Çà»ç°èÈ¹ÀÌ Á¸ÀçÇÏÁö ¾Ê½À´Ï´Ù. Çà»ç°èÈ¹À» ¸ÕÀú µî·ÏÇÏ¼¼¿ä!","N");
			specBtnRow.Enable = false;
			return;
		} else {
			specBtnRow.Enable = true;
		}
		fcSearchHnrRefreWnr();  // ÇØ´ç °øÁö»çÇ×¿¡ ´ëÇÑ ½ÅÃ»³»¿ªÀ» Á¶È¸ÇÑ´Ù.
	} else if ( sServiceName == "rbs1130-service:searchHnrRefreWnr" ) { //  ¼±Á¤ÀÚ Á¶È¸ ¿Ï·á ÈÄ
		//Trace(dsHnrRefreWnr.savexml());
		fnc_SetStatus(SNIS_CRA_0003, dsHnrRefreWnr.rowcount());
	} else if ( sServiceName == "rbs1130-service:saveHnrRefreWnr" ) { // ÀúÀå ¿Ï·á ÈÄ
		fcSearchHnrRefreWnr();  // ´Ù½Ã Á¶È¸ÇÑ´Ù.
	}			
}


