function fcGridSort(obj,nCell,nX,nY) {
	//Æ¯Á¤ Head Å¬¸¯ ½Ã ÇØ´ç Ç×¸ñÀ¸·Î Sort(¸¸¾à Æ¯Á¤ ÄÃ·³À» ´©¸¦¶§¸¸ ¼ÒÆ®ÇÏ°íÀÚ ÇÑ´Ù¸é nCell·Î Á¶°Ç)
