// Æ¯Á¤ÄÞÆ÷³ÍÆ®(±×¸®µå) ¸®»çÀÌÁî
function fcSetResize(obj,nX)
{	// ÇØ´çÈ­¸éÀº ±âÁØ»çÀÌÁîº¸´Ù Height°¡ Å©±â¶§¹®¿¡ ½ºÅ©·Ñ¹Ù »ý¼º¹ß»ý.±×¿¡ µû¶ó ±×¸®µå»çÀÌÁî Á¶Á¤
	var arrObj = array(-1);
	arrObj[0] = "grdHnrRefreWnr";

	var nWidth = nX-18;
	if( nX <= 820){
		// for( var i=0; i<length(arrObj); i++){
			// object(arrObj[i]).Autofit = false;		
			// object(arrObj[i]).right = 788;
			// object(arrObj[i]).Redraw;
		// }
		grdHnrRefreWnr.width = 586;
		grdHnrRefreWnr.AutoFit = false;
		Shape1.Width = 801;	
		divBtnLst.Left = this.width - 405;			
		return;
	}else{
		grdHnrRefreWnr.AutoFit = true;
		grdHnrRefreWnr.Width = nWidth -10 - grdHnrRefreWnr.left;
	}

	// °øÅë
	Shape1.Width = nWidth-15;
	specBtnRow.left = nWidth - 50;	
	divBtnLst.Left = nWidth - 380;	
	btnSendSms.left = nWidth - btnSendSms.width - 20;
}
