function fcInit(obj) {
    dsYesSangList.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {   		

	var sServiceName = "rbs1220-service:searchList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsYesSangList=dsYesSangList ";     	

	fcd_AddParam("MEET_CD"    , rdoMeetCd.value);        // 경륜/경정 구분
	fcd_AddParam("STND_YEAR"  , cboStndYear.value);      //  기준년도
	fcd_AddParam("YES_COM_SEQ", cboYesCom.value);        // 예상지 업체코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

function fcSearchYesSang(obj) {

	var sServiceName = "rbs1220-service:searchYesSang";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsYesSang=dsYesSang ";     	

	fcd_AddParam("MEET_CD"    , rdoMeetCd.value);        // 경륜/경정 구분
	fcd_AddParam("STND_YEAR"  , cboStndYear.value);      //  기준년도
	fcd_AddParam("YES_COM_SEQ", cboYesCom.value);        // 예상지 업체코드

	fcd_AddParam("TMS" ,     dsYesSangList.GetColumn(dsYesSangList.row, "TMS"));        // 회차
	fcd_AddParam("DAY_ORD" , dsYesSangList.GetColumn(dsYesSangList.row, "DAY_ORD"));        // 일차

	if (fnc_isNull(dsYesSangList.GetColumn(dsYesSangList.row, "TMS"))) return;

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;
	nLastRow = dsYesSangList.row;

	var sServiceName = "rbs1220-service:saveYesSang";
	var sInDataSet   = "dsYesSang=dsYesSang:u ";
	var sOutDataSet  = "dsYesSangList=dsYesSangList ";

	fcd_AddParam("MEET_CD"    , rdoMeetCd.value);        // 예상지 업체명	
	fcd_AddParam("STND_YEAR"  , cboStndYear.value);      //  기준년도
	fcd_AddParam("YES_COM_SEQ", cboYesCom.value);        // 예상지 업체코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

