function fcAddRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {
		var nRow = dsYesCom.AddRow(); // Record Add	
		dsYesCom.SetColumn(nRow, "MEET_CD", rdoMeetCd.Value);

		grdList.SetCellPos(1);
		grdList.SetFocus();
	} 
}


