/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if (sServicename == "common-service:searchCode") {	// 공통코드 조회후
		if (dsMeetCd.rowcount() > 0) rdoMeetCd.Value = "001";

		if (dsYesCom.RowCount() > 0) dsYesCom.Filter("MEET_CD='"+rdoMeetCd.Value+"'");
		dsYesCom.row = 0;
	} else if ( sServiceName == "rbs1220-service:searchList" ) {	// 날짜별 리스트
		fcSearchYesSang();
	} else if ( sServiceName == "rbs1220-service:searchYesSang" ) {	// 예상내역  조회 완료 후

	}	else if ( sServiceName == "rbs1220-service:saveYesSang" ) { // 저장 완료 후
		dsYesSangList.row = nLastRow;
		fcSearchYesSang();
	}			


}


