/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    fcInit(obj);  // 그리드 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsYesSang) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	/*
	oDataSet          = dsYesSang;
	sMandatoryColID   = "YES_COM_CD|YES_COM_NM";
	sMandatoryColName = "접속계정|업체명";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}
	*/
	var sYesSang = "";
	for (var iCnt=0;iCnt<dsYesSang.rowcount();iCnt++) {
		if (ToLower(dsYesSang.GetRowType(iCnt)) == "normal") continue;

		sYesSang = dsYesSang.GetColumn(icnt, "YESANG");
		if (fnc_IsNotNull(sYesSang)) {			
			if (!isDigit(sYesSang)) {
				fnc_message("예상내역은 숫자만 입력할 수 있습니다.", "N");
				//dsYesSang.row = icnt;
				return false;
			}
			if (Length(sYesSang) != 4) {	
				fnc_message("예상내역은 4명의 정번을 입력하여야 합니다.", "N");
				//dsYesSang.row = icnt;
				return false;
			}
			if (Pos(sYesSang,"0") >= 0) {
				fnc_message("0번은 입력할 수 없습니다.", "N");
				//dsYesSang.row = icnt;
				return false;
			}
			if (Pos(sYesSang,"0") >= 0) {
				fnc_message("0번은 입력할 수 없습니다.", "N");
				//dsYesSang.row = icnt;
				return false;
			}
			var iStr = "";
			for (var i=0;i<4;i++) {
				iStr = substr(sYesSang, i, 1);
				if (Pos(substr(sYesSang, i+1), iStr) >= 0) {
					fnc_message("정번["+iStr+"]은 중복될 수 없습니다.", "N");
					//dsYesSang.row = icnt;
					return false;
				}
			}
		}
	}

	if (dsYesSangList.GetColumn(dsYesSangList.row, "RACE_DAY") < today()) {
		if (!fnc_message("이미 경주일자가 지난 자료입니다. \n("+stRaceYesSang.text+")\n예상내역을 수정하시겠습니까?", "Y")) return false;
	}

	return true;
}


