/*-------------------------------------------------------------------------------------------------+
>>  경륜경정 년도별 가까운 회차
+-------------------------------------------------------------------------------------------------*/

function fcdsOnRowPosChanged(obj,nOldRow,nRow)
{
	if (nRow >= 0) {
		fcSearchYesSang();
		stRaceYesSang.text = iif(dsYesSangList.GetColumn(dsYesSangList.row, "MEET_CD")=="001","경륜","경정") + " " +
		                     dsYesSangList.GetColumn(dsYesSangList.row, "STND_YEAR") + "년 " +
		                     dsYesSangList.GetColumn(dsYesSangList.row, "TMS") + "회 " +
		                     dsYesSangList.GetColumn(dsYesSangList.row, "DAY_ORD") + "일차";
	}
}


function fcrdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
