/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	sCopyYn = "";

	cboSampReport.index = 0;


	dsReportMana.ClearData();	//보고서 

	dsUploadFile.ClearData();	//첨부파일

	dsReportMng.ClearData();	//양식담당자 

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rbs4010-service:searchReportMana";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportMana=dsReportMana ";

	 //조회조건 : 
	 fcd_AddParam("DEPT_CD", sDeptCd);
	 fcd_AddParam("REQ_STR_DT", calReqStrDt.Value);
	 fcd_AddParam("REQ_END_DT", calReqEndDt.Value);
	 fcd_AddParam("REPORT_NM", edSearchReportNm.Text);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}




/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsReportMana) && !fnc_DatasetChangeCheck(dsReportMng) && !fnc_DatasetChangeCheck(dsUploadFile)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 보고서 필수항목체크
	oDataSet          = dsReportMana;
	sMandatoryColID   = "REQ_DT|SUBM_LIMIT_DT|REPORT_NM";
	sMandatoryColName = "요청일자|제출기한|보고서명";
	oGrid             =  grdReportMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	// 양식담당자 필수항목체크
	oDataSet          = dsReportMng;
	sMandatoryColID   = "MNG_ID|MNG_DEPT_CD";
	sMandatoryColName = "담당자|지점(부서명)";
	oGrid             =  grdReportMng;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	switch ( obj.id ) {
		case "btnSave":
			if ( !fcSaveCheck() ) return;

			if(sCopyYn == ""){  // 양식불러보기  X
				sServiceName = "rbs4010-service:saveReportMana";
				sInDataSet   = "dsReportMana=dsReportMana:u dsUploadFile=dsUploadFile:u dsReportMng=dsReportMng:u";

				sOutDataSet  = "dsReportMana=dsReportMana ";



				//첨부파일 서버 업로드 
				if( dsUploadFile.GetRowCount() > 0 ) {
					for ( i = 0; i < dsUploadFile.GetRowCount(); i++ ) {
						if(dsUploadFile.getColumn(i, "CHK") == "1" ) {
							var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
														, dsUploadFile.GetColumn(i, "FILE_PATH" )
														, dsUploadFile.GetColumn(i, "FILE_NM" ) 
														,"");

							var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

							if ( rtn_arr[0] != "SUCC" ) {
								alert(rtn_arr[1] + "파일 업로드 에러발생");
								return false;
							}


							dsUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
							dsUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

						} // end if
					}
				} // end 

			}else{  // 양식불러오기 후 처리 

				sServiceName = "rbs4010-service:saveReportMana";
				sInDataSet   = "dsReportMana=dsReportMana:u dsUploadFile=dsUploadFile:a dsReportMng=dsReportMng:a";

				sOutDataSet  = "dsReportMana=dsReportMana ";
			}


			fcd_AddParam("DEPT_CD", dsReportMana.GetColumn(dsReportMana.row,"DEPT_CD"));
			fcd_AddParam("STND_YEAR", dsReportMana.GetColumn(dsReportMana.row,"STND_YEAR"));
			fcd_AddParam("SEQ", dsReportMana.GetColumn(dsReportMana.row,"SEQ"));

			sLastSaveRow = -1;			
			for ( i = 0; i < dsReportMana.GetRowCount(); i++ ) {
				if(dsReportMana.GetRowTypeNF(i) == "insert" || dsReportMana.GetRowTypeNF(i) == "update"){
					sLastSaveRow = i;
				}

			}


			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

			break;

	}


}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" : //보고서 그리드 행추가,삭제 
			if ( fnc_DatasetChangeCheck(dsReportMana) ) {
				fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				return;
			}

			nRow = dsReportMana.AddRow(); // Record Add

			dsReportMana.SetColumn(nRow,"STND_YEAR",sCurYear );
			dsReportMana.SetColumn(nRow,"DEPT_CD",sDeptCd );
			dsReportMana.SetColumn(nRow,"REQ_DT",TODAY() );


			calSubmLimitDt.SetFocus();// 입력 첫 항목으로 Focus

		  break;

	    case "fileBtnRow" : //양식담당자 그리드 행추가,삭제 
	    	if ( dsReportMana.GetRowCount() < 0) {

				return;
			}


			sResponse = fcd_GetSelectFile();	
			row = dsUploadFile.addRow();

			dsUploadFile.SetColumn(row, "ATT_FILE_KEY" , dsReportMana.GetColumn(dsReportMana.currow,"ATT_FILE_KEY"));


			if ( !fnc_IsNull(sResponse) ) {
				dsUploadFile.SetColumn(row, "CHK"       , "1"         );
				dsUploadFile.SetColumn(row, "FILE_PATH"  , sResponse[0] + "\\" + sResponse[1]);
				dsUploadFile.SetColumn(row, "FILE_NM" , sResponse[1]);


			}	

			if ( dsUploadFile.getColumn(row, "CHK") != "1") {
				dsUploadFile.rowcount = dsUploadFile.DeleteRow(row);
			}

			break;

		case "mngBtnRow" : //파일담당자 그리드 행추가,삭제 
		    if ( dsReportMana.GetRowCount() < 0) {

				return;
			}


						//조직도 선택
			sUrl = "common::comDeptTree.xml";
			sArgs = "";	

			arrReturn = Dialog(sUrl, sArgs, 334, 443, "");

			if(arrReturn.length > 0){

				nRow = dsReportMng.AddRow(); // Record Add

				dsReportMng.SetColumn(nRow,"MNG_ID",arrReturn[0]);
				dsReportMng.SetColumn(nRow,"MNG_NM",arrReturn[1]);
				dsReportMng.SetColumn(nRow,"MNG_DEPT_CD",arrReturn[4]);
				dsReportMng.SetColumn(nRow,"MNG_DEPT_NM",arrReturn[5]);


				dsReportMng.SetColumn(nRow,"DEPT_CD",dsReportMana.GetColumn(dsReportMana.currow,"DEPT_CD") );
				dsReportMng.SetColumn(nRow,"STND_YEAR",dsReportMana.GetColumn(dsReportMana.currow,"STND_YEAR") );
				dsReportMng.SetColumn(nRow,"SEQ",dsReportMana.GetColumn(dsReportMana.currow,"SEQ") );

			}



			break;

	}
}



/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :	//보고서 그리드 행추가,삭제 
			if ( !fnc_DeleteCheck(dsReportMana, "chk", "1") ) return;

			dsUploadFile.ClearData();
			dsReportMng.ClearData();

			break;

		case "mngBtnRow" : //양식담당자 그리드 행추가,삭제 
			if ( !fnc_DeleteCheck(dsReportMng, "chk", "1") ) return;

			break;

		case "fileBtnRow" : //파일담당자 그리드 행추가,삭제 
			if ( !fnc_DeleteCheck(dsUploadFile, "chk", "1") ) return;

			break;	

	}
}



