/*-------------------------------------------------------------------------------------------------+
>>  Grid OnExpandUp event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{

		switch ( obj.id ) {
			case "grdReportMng" :	//보고서 담당자 

				if(nCell == 2){     //담당자 

						sUrl = "common::comDeptTree.xml";
						sArgs = "";	

						arrReturn = Dialog(sUrl, sArgs, 334, 443, "");

						dsReportMng.SetColumn(nRow,"MNG_ID",arrReturn[0]);
						dsReportMng.SetColumn(nRow,"MNG_NM",arrReturn[1]);
						dsReportMng.SetColumn(nRow,"MNG_DEPT_CD",arrReturn[4]);
						dsReportMng.SetColumn(nRow,"MNG_DEPT_NM",arrReturn[5]);

			    }

			break;
		}
}

/*-------------------------------------------------------------------------------------------------+
>>  dataset  OnRowPosChanged event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	 switch ( obj.id ) {
		//장비 목록 
		case "dsReportMana" :	// 보고서
			 if(nRow>-1){	
				if(dsReportMana.GetRowType(dsReportMana.row) ==  "insert"){
						dsUploadFile.ClearData();
						dsReportMng.ClearData();
				}else if(dsReportMana.GetRowType(dsReportMana.row) ==  "normal" || dsReportMana.GetRowType(dsReportMana.row) ==  "update"){
						fcDtlSearch();
				}
			}
		break;
	 }

}


/*-------------------------------------------------------------------------------------------------+
>>  dataset  OnLoadCompleted event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {

		case "dsReportManaSamp" : //양식불러오기 dataset 
			dsReportManaSamp.InsertRow(0);
			dsReportManaSamp.SetColumn(0, "SEQ", -1);
			dsReportManaSamp.SetColumn(0, "REPORT_NM", "선택");
			cboSampReport.index = 0;

			break;
	 }
}


/*-------------------------------------------------------------------------------------------------+
>>  combo  OnChanged event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {

		case "cboSampReport" :  //양식불러오기 콤보 선택 

			if(obj.value > 0){
				dsUploadFile.ClearData();	//첨부파일

				dsReportMng.ClearData();	//양식담당자 

				dsReportMana.ClearData();

				fcSearchReportCopy(nNewIndex);
			}

			break;
	 }
}


/*-------------------------------------------------------------------------------------------------+
>>  Grid   OnCellDBLClick  event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {
		//보고서양식 선택 
		case "grdUploadFile" :

				var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFile.GetColumn(nRow, "FILE_NM"));
				var sServerFileName = dsUploadFile.GetColumn(nRow, "FILE_PATH");
				var sFileSize       = dsUploadFile.GetColumn(nRow, "FILE_SIZE");
				if ( sLocalFileName == null ) return;

				fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFile);

			break;
	 }
}

